import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import './GuestDashboard.css';

const GuestDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myReservations, setMyReservations] = useState([]);
  const [activeReservations, setActiveReservations] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGuestStats();
  }, []);

  const fetchGuestStats = async () => {
    try {
      const response = await axios.get('/api/reservations');
      const reservations = response.data.reservations || [];
      setMyReservations(reservations);
      setActiveReservations(reservations.filter(r => ['confirmed', 'checked-in'].includes(r.status)).length);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="guest-loading">
        <div className="loading-spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'confirmed': return 'status-confirmed';
      case 'checked-in': return 'status-checked-in';
      case 'checked-out': return 'status-checked-out';
      case 'cancelled': return 'status-cancelled';
      default: return 'status-pending';
    }
  };

  return (
    <div className="guest-dashboard">
      {/* Header Section */}
      <div className="dashboard-header">
        <div className="header-content">
          <div className="welcome-section">
            <div className="user-avatar">
              {user?.firstName?.charAt(0)}{user?.lastName?.charAt(0)}
            </div>
            <div className="welcome-text">
              <h1 className="welcome-title">
                Welcome back, <span className="user-name">{user?.firstName}</span>
              </h1>
              <p className="welcome-subtitle">Manage your reservations and bookings</p>
            </div>
          </div>
          <button onClick={handleLogout} className="logout-button">
            Logout
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-container">
        <div className="stat-card">
          <div className="stat-icon">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M9 11l3 3L22 4"></path>
              <path d="M21 12v7a2 2 0 01-2 2H5a2 2 0 01-2-2V5a2 2 0 012-2h11"></path>
            </svg>
          </div>
          <div className="stat-info">
            <h3 className="stat-label">My Reservations</h3>
            <p className="stat-value">{myReservations.length}</p>
            <span className="stat-desc">Total Bookings</span>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="12" cy="12" r="10"></circle>
              <path d="M12 6v6l4 2"></path>
            </svg>
          </div>
          <div className="stat-info">
            <h3 className="stat-label">Active Bookings</h3>
            <p className="stat-value">{activeReservations}</p>
            <span className="stat-desc">Currently Active</span>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"></path>
            </svg>
          </div>
          <div className="stat-info">
            <h3 className="stat-label">Loyalty Status</h3>
            <p className="stat-value">Gold</p>
            <span className="stat-desc">Premium Member</span>
          </div>
        </div>
      </div>

      {/* Recent Reservations */}
      <div className="reservations-container">
        <div className="section-header">
          <h2 className="section-title">Recent Reservations</h2>
          <button className="view-all-button" onClick={() => navigate('/reservations')}>
            View All
          </button>
        </div>

        {myReservations.length > 0 ? (
          <div className="reservations-list">
            {myReservations.slice(0, 5).map((reservation) => (
              <div key={reservation._id} className="reservation-card">
                <div className="reservation-main">
                  <div className="reservation-id">
                    <span className="confirmation-label">Confirmation #</span>
                    <span className="confirmation-number">{reservation.confirmationNumber}</span>
                  </div>
                  <div className={`status-badge ${getStatusColor(reservation.status)}`}>
                    {reservation.status}
                  </div>
                </div>
                <div className="reservation-details">
                  <div className="detail-row">
                    <div className="detail-item">
                      <span className="detail-label">Room</span>
                      <span className="detail-value">
                        {reservation.roomId?.roomNumber} - {reservation.roomId?.roomType}
                      </span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Check-in</span>
                      <span className="detail-value">
                        {new Date(reservation.checkInDate).toLocaleDateString('en-US', { 
                          month: 'short', 
                          day: 'numeric', 
                          year: 'numeric' 
                        })}
                      </span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Check-out</span>
                      <span className="detail-value">
                        {new Date(reservation.checkOutDate).toLocaleDateString('en-US', { 
                          month: 'short', 
                          day: 'numeric', 
                          year: 'numeric' 
                        })}
                      </span>
                    </div>
                    <div className="detail-item">
                      <span className="detail-label">Amount</span>
                      <span className="detail-value amount">${reservation.totalAmount?.toFixed(2)}</span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-icon">
              <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5">
                <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                <polyline points="9 22 9 12 15 12 15 22"></polyline>
              </svg>
            </div>
            <h3 className="empty-title">No Reservations Yet</h3>
            <p className="empty-text">Start your luxury journey by booking your first stay</p>
            <button className="primary-button" onClick={() => navigate('/rooms')}>
              Browse Rooms
            </button>
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="quick-actions-container">
        <h2 className="section-title">Quick Actions</h2>
        <div className="actions-grid">
          <button className="action-button" onClick={() => navigate('/reservations')}>
            <div className="action-icon">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M9 11l3 3L22 4"></path>
                <path d="M21 12v7a2 2 0 01-2 2H5a2 2 0 01-2-2V5a2 2 0 012-2h11"></path>
              </svg>
            </div>
            <span className="action-label">Reservations</span>
          </button>

          <button className="action-button" onClick={() => navigate('/rooms')}>
            <div className="action-icon">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                <polyline points="9 22 9 12 15 12 15 22"></polyline>
              </svg>
            </div>
            <span className="action-label">Browse Rooms</span>
          </button>

          <button className="action-button" onClick={() => navigate('/services')}>
            <div className="action-icon">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z"></path>
              </svg>
            </div>
            <span className="action-label">Services</span>
          </button>

          <button className="action-button" onClick={() => navigate('/feedback')}>
            <div className="action-icon">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M21 15a2 2 0 01-2 2H7l-4 4V5a2 2 0 012-2h14a2 2 0 012 2z"></path>
              </svg>
            </div>
            <span className="action-label">Feedback</span>
          </button>

          <button className="action-button" onClick={() => navigate('/billing')}>
            <div className="action-icon">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <rect x="1" y="4" width="22" height="16" rx="2" ry="2"></rect>
                <line x1="1" y1="10" x2="23" y2="10"></line>
              </svg>
            </div>
            <span className="action-label">Billing</span>
          </button>

          <button className="action-button" onClick={() => navigate('/gallery')}>
            <div className="action-icon">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                <circle cx="8.5" cy="8.5" r="1.5"></circle>
                <polyline points="21 15 16 10 5 21"></polyline>
              </svg>
            </div>
            <span className="action-label">Gallery</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default GuestDashboard;
