import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import './GuestDashboard.css'; // Import the CSS file

const GuestDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myReservations, setMyReservations] = useState([]);
  const [activeReservations, setActiveReservations] = useState(0);
  const [loading, setLoading] = useState(true);
  const [currentSlide, setCurrentSlide] = useState(0);

  const carouselImages = [
    'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=1920&q=80',
    'https://images.unsplash.com/photo-1631049307264-da0ec9d70304?w=1920&q=80',
    'https://images.unsplash.com/photo-1571896349842-33c89424de2d?w=1920&q=80'
  ];

  useEffect(() => {
    fetchGuestStats();
  }, []);

  // Auto carousel effect
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % carouselImages.length);
    }, 4000); // Change slide every 4 seconds

    return () => clearInterval(interval);
  }, [carouselImages.length]);

  const fetchGuestStats = async () => {
    try {
      const response = await axios.get('/api/reservations');
      const reservations = response.data.reservations || [];
      setMyReservations(reservations);
      setActiveReservations(reservations.filter(r => ['confirmed', 'checked-in'].includes(r.status)).length);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner-wrapper">
          <div className="spinner"></div>
          <p className="loading-text">Loading your luxury experience...</p>
        </div>
      </div>
    );
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getStatusClass = (status) => {
    switch(status) {
      case 'confirmed': return 'confirmed';
      case 'checked-in': return 'checked-in';
      case 'checked-out': return 'checked-out';
      case 'cancelled': return 'cancelled';
      default: return 'pending';
    }
  };

  return (
    <div className="guest-dashboard-container">
      {/* Image Carousel */}
      <div className="dashboard-carousel">
        <div 
          className="carousel-wrapper"
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {carouselImages.map((image, index) => (
            <div key={index} className="carousel-slide">
              <img src={image} alt={`Luxury Stay ${index + 1}`} className="carousel-image" />
              <div className="carousel-overlay"></div>
            </div>
          ))}
        </div>

        {/* Carousel Indicators */}
        <div className="carousel-indicators">
          {carouselImages.map((_, index) => (
            <button
              key={index}
              className={`carousel-indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
      </div>

      <div className="dashboard-content-wrapper">

        {/* Header Card */}
        <div className="glass-card" style={{ marginBottom: '2rem' }}>
          <div className="dashboard-header">
            <div className="header-content">
              <div className="header-icon">
                <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                  <polyline points="9 22 9 12 15 12 15 22"></polyline>
                </svg>
              </div>
              <div>
                <h1 className="header-title">Luxury Guest Portal</h1>
                <p className="header-subtitle">
                  Welcome back, <span className="user-name">{user?.firstName}</span>! Experience excellence at every moment
                </p>
              </div>
            </div>
            <button onClick={handleLogout} className="logout-btn">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M9 21H5a2 2 0 01-2-2V5a2 2 0 012-2h4"></path>
                <polyline points="16 17 21 12 16 7"></polyline>
                <line x1="21" y1="12" x2="9" y2="12"></line>
              </svg>
              <span>Logout</span>
            </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="stats-grid">
          <div className="stat-card">
          <div className="stat-content">
              <div>
                <h3 className="stat-label">Total Reservations</h3>
              <p className="stat-value">{myReservations.length}</p>
                <div className="stat-description">
                  <span className="trend-up">â†‘ 12%</span> from last month
                </div>
              </div>
              <div className="stat-icon">
                <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <rect x="3" y="4" width="18" height="18" rx="2" ry="2"></rect>
                  <line x1="16" y1="2" x2="16" y2="6"></line>
                  <line x1="8" y1="2" x2="8" y2="6"></line>
                  <line x1="3" y1="10" x2="21" y2="10"></line>
                </svg>
              </div>
            </div>
            <div className="stat-progress">
              <div className="stat-progress-bar" style={{ width: '75%' }}></div>
            </div>
        </div>

          <div className="stat-card">
          <div className="stat-content">
              <div>
            <h3 className="stat-label">Active Bookings</h3>
              <p className="stat-value">{activeReservations}</p>
                <div className="stat-description">
                  <span className="trend-up">â†‘ 8%</span> occupancy rate
            </div>
          </div>
              <div className="stat-icon">
                <svg width="40" height="40" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                  <polyline points="9 22 9 12 15 12 15 22"></polyline>
                </svg>
              </div>
          </div>
            <div className="stat-progress">
              <div className="stat-progress-bar" style={{ width: '100%' }}></div>
            </div>
        </div>
      </div>

        {/* Reservations Table */}
        <div className="glass-card" style={{ marginBottom: '2rem' }}>
          <div className="table-header">
          <h2 className="section-title">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
              </svg>
              <span>Recent Reservations</span>
          </h2>
        </div>

        {myReservations.length > 0 ? (
            <div style={{ overflowX: 'auto' }}>
              <table className="reservations-table">
                <thead>
                  <tr>
                    <th>CONFIRMATION</th>
                    <th>ROOM DETAILS</th>
                    <th>CHECK-IN</th>
                    <th>CHECK-OUT</th>
                    <th>STATUS</th>
                    <th>AMOUNT</th>
                  </tr>
                </thead>
                <tbody>
                  {myReservations.slice(0, 5).map(reservation => (
                    <tr key={reservation._id}>
                      <td>
                        <span className="confirmation-number">
                          {reservation.confirmationNumber}
                        </span>
                      </td>
                      <td>
                        <div className="room-info">Room {reservation.roomId?.roomNumber}</div>
                        <div className="room-type">{reservation.roomId?.roomType}</div>
                      </td>
                      <td style={{ fontWeight: '600' }}>
                        {new Date(reservation.checkInDate).toLocaleDateString('en-US', { 
                          month: 'short', 
                          day: 'numeric',
                          year: 'numeric'
                        })}
                      </td>
                      <td style={{ fontWeight: '600' }}>
                        {new Date(reservation.checkOutDate).toLocaleDateString('en-US', { 
                          month: 'short', 
                          day: 'numeric',
                          year: 'numeric'
                        })}
                      </td>
                      <td>
                        <span className={`status-badge ${getStatusClass(reservation.status)}`}>
                          {reservation.status}
                      </span>
                      </td>
                      <td className="amount-cell">
                        ${reservation.totalAmount?.toFixed(2)}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
          </div>
        ) : (
          <div className="empty-state">
              <div className="empty-state-icon">
                <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                  <polyline points="9 22 9 12 15 12 15 22"></polyline>
                </svg>
              </div>
              <p className="empty-state-text">No reservations found</p>
              <p style={{ color: '#A0826D', marginTop: '0.5rem' }}>
                Start your luxury journey by booking a room
              </p>
          </div>
        )}
      </div>

      {/* Quick Actions */}
        <div className="glass-card">
          <h2 className="actions-section-title">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <polygon points="13 2 3 14 12 14 11 22 21 10 12 10 13 2"></polygon>
            </svg>
            <span>Quick Actions</span>
        </h2>
        <div className="actions-grid">
            <a href="/reservations" className="action-button">
              <div className="action-icon">
                <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
                  <circle cx="12" cy="12" r="3"></circle>
                </svg>
              </div>
              <span className="action-label">View All Reservations</span>
            </a>

            <a href="/rooms" className="action-button">
              <div className="action-icon">
                <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                  <polyline points="9 22 9 12 15 12 15 22"></polyline>
                </svg>
              </div>
              <span className="action-label">Browse Rooms</span>
            </a>

            <a href="/services" className="action-button">
              <div className="action-icon">
                <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M22 16.92v3a2 2 0 01-2.18 2 19.79 19.79 0 01-8.63-3.07 19.5 19.5 0 01-6-6 19.79 19.79 0 01-3.07-8.67A2 2 0 014.11 2h3a2 2 0 012 1.72 12.84 12.84 0 00.7 2.81 2 2 0 01-.45 2.11L8.09 9.91a16 16 0 006 6l1.27-1.27a2 2 0 012.11-.45 12.84 12.84 0 002.81.7A2 2 0 0122 16.92z"></path>
