import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import { Hotel, Calendar, DollarSign, LogOut, Eye, Bed, Sparkles, FileText, CreditCard } from 'lucide-react';

const GuestDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myReservations, setMyReservations] = useState([]);
  const [activeReservations, setActiveReservations] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGuestStats();
  }, []);

  const fetchGuestStats = async () => {
    try {
      const response = await axios.get('/api/reservations');
      const reservations = response.data.reservations || [];
      setMyReservations(reservations);
      setActiveReservations(reservations.filter(r => ['confirmed', 'checked-in'].includes(r.status)).length);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center">
        <div className="relative">
          <div className="w-20 h-20 border-4 border-amber-500 border-t-transparent rounded-full animate-spin"></div>
          <Hotel className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 text-amber-500" size={32} />
        </div>
      </div>
    );
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-6">
      {/* Animated Background Elements */}
      <div className="fixed inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-20 left-20 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute top-40 right-20 w-72 h-72 bg-amber-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-700"></div>
        <div className="absolute bottom-20 left-1/2 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-1000"></div>
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Header */}
        <div className="backdrop-blur-xl bg-white/10 rounded-3xl border border-white/20 p-8 mb-8 shadow-2xl hover:shadow-amber-500/20 transition-all duration-500 hover:scale-[1.01]">
          <div className="flex justify-between items-center">
            <div className="flex items-center gap-4">
              <div className="bg-gradient-to-br from-amber-400 to-amber-600 p-4 rounded-2xl shadow-lg">
                <Hotel className="text-white" size={32} />
              </div>
              <div>
                <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-amber-200 via-amber-400 to-amber-200 animate-gradient">
                  Guest Portal
                </h1>
                <p className="text-gray-300 mt-2 text-lg">
                  Welcome back, <span className="text-amber-400 font-semibold">{user?.firstName}</span>! Your luxury experience awaits
                </p>
              </div>
            </div>
            <button 
              onClick={handleLogout}
              className="group backdrop-blur-xl bg-red-500/20 hover:bg-red-500/30 text-red-300 hover:text-white px-6 py-3 rounded-xl border border-red-400/30 hover:border-red-400 transition-all duration-300 flex items-center gap-2 hover:scale-105 hover:shadow-lg hover:shadow-red-500/50"
            >
              <LogOut size={20} className="group-hover:rotate-12 transition-transform duration-300" />
              <span className="font-semibold">Logout</span>
            </button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div className="group backdrop-blur-xl bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-3xl border border-white/20 p-8 shadow-2xl hover:shadow-blue-500/30 transition-all duration-500 hover:scale-105 hover:-translate-y-2">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-gray-300 text-lg mb-2 font-medium">Total Reservations</h3>
                <p className="text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-300 to-purple-300">
                  {myReservations.length}
                </p>
              </div>
              <div className="bg-gradient-to-br from-blue-400 to-purple-500 p-6 rounded-2xl shadow-lg group-hover:rotate-12 transition-transform duration-500">
                <Calendar className="text-white" size={40} />
              </div>
            </div>
            <div className="mt-4 h-2 bg-white/10 rounded-full overflow-hidden">
              <div className="h-full bg-gradient-to-r from-blue-400 to-purple-500 rounded-full w-3/4 animate-pulse"></div>
            </div>
          </div>

          <div className="group backdrop-blur-xl bg-gradient-to-br from-green-500/20 to-emerald-500/20 rounded-3xl border border-white/20 p-8 shadow-2xl hover:shadow-green-500/30 transition-all duration-500 hover:scale-105 hover:-translate-y-2">
            <div className="flex items-center justify-between">
              <div>
                <h3 className="text-gray-300 text-lg mb-2 font-medium">Active Bookings</h3>
                <p className="text-6xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-green-300 to-emerald-300">
                  {activeReservations}
                </p>
              </div>
              <div className="bg-gradient-to-br from-green-400 to-emerald-500 p-6 rounded-2xl shadow-lg group-hover:rotate-12 transition-transform duration-500">
                <Bed className="text-white" size={40} />
              </div>
            </div>
            <div className="mt-4 h-2 bg-white/10 rounded-full overflow-hidden">
              <div className="h-full bg-gradient-to-r from-green-400 to-emerald-500 rounded-full w-full animate-pulse"></div>
            </div>
          </div>
        </div>

        {/* Reservations Table */}
        <div className="backdrop-blur-xl bg-white/10 rounded-3xl border border-white/20 p-8 mb-8 shadow-2xl hover:shadow-purple-500/20 transition-all duration-500">
          <h2 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-amber-200 to-amber-400 mb-6 flex items-center gap-3">
            <Sparkles className="text-amber-400" size={28} />
            Recent Reservations
          </h2>
          
          {myReservations.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-white/20">
                    <th className="text-left py-4 px-4 text-amber-300 font-semibold">Confirmation #</th>
                    <th className="text-left py-4 px-4 text-amber-300 font-semibold">Room</th>
                    <th className="text-left py-4 px-4 text-amber-300 font-semibold">Check-in</th>
                    <th className="text-left py-4 px-4 text-amber-300 font-semibold">Check-out</th>
                    <th className="text-left py-4 px-4 text-amber-300 font-semibold">Status</th>
                    <th className="text-left py-4 px-4 text-amber-300 font-semibold">Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {myReservations.slice(0, 5).map((reservation, index) => (
                    <tr 
                      key={reservation._id}
                      className="border-b border-white/10 hover:bg-white/5 transition-all duration-300 group"
                      style={{ animationDelay: `${index * 100}ms` }}
                    >
                      <td className="py-4 px-4 text-gray-300 font-mono">{reservation.confirmationNumber}</td>
                      <td className="py-4 px-4 text-gray-200">
                        <div className="font-semibold">{reservation.roomId?.roomNumber}</div>
                        <div className="text-sm text-gray-400">{reservation.roomId?.roomType}</div>
                      </td>
                      <td className="py-4 px-4 text-gray-300">{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                      <td className="py-4 px-4 text-gray-300">{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                      <td className="py-4 px-4">
                        <span className={`px-3 py-1 rounded-full text-sm font-semibold backdrop-blur-xl ${
                          reservation.status === 'confirmed' ? 'bg-green-500/20 text-green-300 border border-green-400/30' :
                          reservation.status === 'checked-in' ? 'bg-blue-500/20 text-blue-300 border border-blue-400/30' :
                          reservation.status === 'checked-out' ? 'bg-gray-500/20 text-gray-300 border border-gray-400/30' :
                          reservation.status === 'cancelled' ? 'bg-red-500/20 text-red-300 border border-red-400/30' :
                          'bg-yellow-500/20 text-yellow-300 border border-yellow-400/30'
                        }`}>
                          {reservation.status}
                        </span>
                      </td>
                      <td className="py-4 px-4 text-amber-400 font-bold text-lg">${reservation.totalAmount?.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-12">
              <Hotel className="mx-auto text-gray-500 mb-4" size={64} />
              <p className="text-gray-400 text-lg">No reservations yet. Start your luxury journey today!</p>
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <div className="backdrop-blur-xl bg-white/10 rounded-3xl border border-white/20 p-8 shadow-2xl">
          <h2 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-amber-200 to-amber-400 mb-6">
            Quick Actions
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
            {[
              { href: '/reservations', icon: Eye, label: 'View Reservations', color: 'from-blue-500 to-blue-600' },
              { href: '/rooms', icon: Bed, label: 'Browse Rooms', color: 'from-purple-500 to-purple-600' },
              { href: '/services', icon: Sparkles, label: 'Request Service', color: 'from-pink-500 to-pink-600' },
              { href: '/feedback', icon: FileText, label: 'Leave Feedback', color: 'from-amber-500 to-amber-600' },
              { href: '/billing', icon: CreditCard, label: 'View Bills', color: 'from-green-500 to-green-600' }
            ].map((action, index) => (
              <a
                key={index}
                href={action.href}
                className="group backdrop-blur-xl bg-white/10 hover:bg-white/20 rounded-2xl border border-white/20 hover:border-white/40 p-6 transition-all duration-500 hover:scale-110 hover:-translate-y-2 hover:shadow-2xl text-center"
              >
                <div className={`bg-gradient-to-br ${action.color} p-4 rounded-xl mx-auto w-fit mb-3 group-hover:rotate-12 transition-transform duration-500 shadow-lg`}>
                  <action.icon className="text-white" size={28} />
                </div>
                <span className="text-white font-semibold text-sm">{action.label}</span>
              </a>
            ))}
          </div>
        </div>
      </div>

      <style>{`
        @keyframes gradient {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }
        .animate-gradient {
          background-size: 200% 200%;
          animation: gradient 3s ease infinite;
        }
        .delay-700 {
          animation-delay: 700ms;
        }
        .delay-1000 {
          animation-delay: 1000ms;
        }
      `}</style>
    </div>
  );
};

export default GuestDashboard;
