import React, { useEffect, useRef, useState } from 'react';

const LuxuryHotelHome = () => {
  const [scrollY, setScrollY] = useState(0);
  const statsRef = useRef(null);
  const [statsAnimated, setStatsAnimated] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && !statsAnimated) {
            setStatsAnimated(true);
            animateStats();
          }
        });
      },
      { threshold: 0.5 }
    );

    if (statsRef.current) {
      observer.observe(statsRef.current);
    }

    return () => {
      if (statsRef.current) {
        observer.unobserve(statsRef.current);
      }
    };
  }, [statsAnimated]);

  const animateStats = () => {
    const stats = [
      { id: 'stat1', target: 50000, suffix: '+' },
      { id: 'stat2', target: 150, suffix: '+' },
      { id: 'stat3', target: 25, suffix: '+' },
      { id: 'stat4', target: 98, suffix: '%' }
    ];

    stats.forEach((stat) => {
      const element = document.getElementById(stat.id);
      if (!element) return;

      const duration = 2000;
      const increment = stat.target / (duration / 16);
      let current = 0;

      const updateCounter = () => {
        current += increment;
        if (current < stat.target) {
          element.textContent = Math.floor(current).toLocaleString() + stat.suffix;
          requestAnimationFrame(updateCounter);
        } else {
          element.textContent = stat.target.toLocaleString() + stat.suffix;
        }
      };
      updateCounter();
    });
  };

  return (
    <div style={styles.container}>
      <style>{keyframes}</style>
      
      {/* Hero Section */}
      <section style={{...styles.hero, transform: `translateY(${scrollY * 0.5}px)`}}>
        <div style={styles.heroOverlay}></div>
        <div style={styles.heroContent}>
          <h1 style={styles.heroTitle}>
            Welcome to LuxuryStay Hospitality
          </h1>
          <p style={styles.heroSubtitle}>
            Experience unparalleled luxury and exceptional service at our world-class hotels
          </p>
          <div style={styles.heroButtons}>
            <button style={{...styles.btn, ...styles.btnPrimary}}>
              Get Started
            </button>
            <button style={{...styles.btn, ...styles.btnOutline}}>
              Sign In
            </button>
          </div>
        </div>
        <div style={styles.scrollIndicator}>
          <span style={styles.scrollText}>Scroll to explore</span>
          <div style={styles.scrollArrow}>‚Üì</div>
        </div>
      </section>

      {/* Statistics Section */}
      <section ref={statsRef} style={styles.statsSection}>
        <div style={styles.statsGrid}>
          <div style={styles.statCard}>
            <div id="stat1" style={styles.statNumber}>0</div>
            <div style={styles.statLabel}>Happy Guests</div>
          </div>
          <div style={styles.statCard}>
            <div id="stat2" style={styles.statNumber}>0</div>
            <div style={styles.statLabel}>Luxury Rooms</div>
          </div>
          <div style={styles.statCard}>
            <div id="stat3" style={styles.statNumber}>0</div>
            <div style={styles.statLabel}>Awards Won</div>
          </div>
          <div style={styles.statCard}>
            <div id="stat4" style={styles.statNumber}>0</div>
            <div style={styles.statLabel}>% Satisfaction</div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section style={styles.featuresSection}>
        <h2 style={styles.sectionTitle}>Why Choose LuxuryStay?</h2>
        <p style={styles.sectionSubtitle}>
          Discover the perfect blend of comfort, elegance, and world-class service
        </p>
        <div style={styles.featuresGrid}>
          {features.map((feature, index) => (
            <div key={index} style={{...styles.featureCard, animationDelay: `${index * 0.1}s`}}>
              <div style={styles.featureIcon}>{feature.icon}</div>
              <h3 style={styles.featureTitle}>{feature.title}</h3>
              <p style={styles.featureText}>{feature.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Testimonials Section */}
      <section style={styles.testimonialsSection}>
        <h2 style={styles.sectionTitle}>What Our Guests Say</h2>
        <p style={styles.sectionSubtitle}>
          Don't just take our word for it - hear from our satisfied guests
        </p>
        <div style={styles.testimonialsGrid}>
          {testimonials.map((testimonial, index) => (
            <div key={index} style={{...styles.testimonialCard, animationDelay: `${index * 0.2}s`}}>
              <div style={styles.rating}>‚≠ê‚≠ê‚≠ê‚≠ê‚≠ê</div>
              <p style={styles.testimonialText}>{testimonial.text}</p>
              <div style={styles.authorSection}>
                <div style={styles.avatar}>{testimonial.initials}</div>
                <div>
                  <div style={styles.authorName}>{testimonial.name}</div>
                  <div style={styles.authorTitle}>{testimonial.title}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Services Section */}
      <section style={styles.servicesSection}>
        <h2 style={styles.sectionTitle}>Our Services</h2>
        <div style={styles.servicesGrid}>
          {services.map((service, index) => (
            <div key={index} style={styles.serviceCard}>
              <h4 style={styles.serviceTitle}>{service.title}</h4>
              <p style={styles.serviceText}>{service.description}</p>
            </div>
          ))}
        </div>
      </section>

      {/* CTA Section */}
      <section style={styles.ctaSection}>
        <h2 style={styles.ctaTitle}>Ready to Experience Luxury?</h2>
        <p style={styles.ctaText}>
          Join thousands of satisfied guests who have made LuxuryStay their preferred choice
        </p>
        <div style={styles.ctaButtons}>
          <button style={{...styles.btn, ...styles.btnPrimary, ...styles.btnLarge}}>
            Create Account
          </button>
          <button style={{...styles.btn, ...styles.btnOutline, ...styles.btnLarge}}>
            Sign In
          </button>
        </div>
      </section>

      {/* Footer */}
      <footer style={styles.footer}>
        <div style={styles.footerContent}>
          <div style={styles.footerSection}>
            <h4 style={styles.footerHeading}>LuxuryStay Hospitality</h4>
            <p style={styles.footerText}>Your gateway to luxury and comfort</p>
          </div>
          <div style={styles.footerSection}>
            <h4 style={styles.footerHeading}>Quick Links</h4>
            <a href="/login" style={styles.footerLink}>Login</a>
            <a href="/register" style={styles.footerLink}>Register</a>
          </div>
          <div style={styles.footerSection}>
            <h4 style={styles.footerHeading}>Contact</h4>
            <p style={styles.footerText}>Email: info@luxurystay.com</p>
            <p style={styles.footerText}>Phone: +1 (555) 123-4567</p>
          </div>
        </div>
        <div style={styles.footerBottom}>
          <p>&copy; 2024 LuxuryStay Hospitality. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

