// Role-based component wrapper - must be inside AuthProvider
const RoleBasedComponent = ({ adminComponent, guestComponent, housekeepingComponent, receptionistComponent, defaultComponent }) => {
  const { user } = useContext(AuthContext);
  
  if (!user) return defaultComponent || adminComponent;
  
  switch (user.role) {
    case 'admin':
    case 'manager':
      return adminComponent;
    case 'guest':
      return guestComponent;
    case 'housekeeping':
      return housekeepingComponent;
    case 'receptionist':
      return receptionistComponent || defaultComponent || adminComponent;
    default:
      return defaultComponent || adminComponent;
  }
};

// Wrapper component for routes that need role-based rendering
const AppRoutes = () => {
  return (
    <Routes>
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route
                path="/dashboard"
                element={
                  <PrivateRoute>
                    <RoleBasedDashboard />
                  </PrivateRoute>
                }
              />
              {/* Users - Admin/Manager only */}
              <Route
                path="/users"
                element={
                  <PrivateRoute allowedRoles={['admin', 'manager']}>
                    <AdminUsers />
                  </PrivateRoute>
                }
              />
              {/* Rooms - Role-based */}
              <Route
                path="/rooms"
                element={
                  <PrivateRoute>
                    <RoleBasedComponent
                      adminComponent={<AdminRooms />}
                      guestComponent={<GuestRooms />}
                      receptionistComponent={<ReceptionistRooms />}
                      defaultComponent={<AdminRooms />}
                    />
                  </PrivateRoute>
                }
              />
              {/* Reservations - Role-based */}
              <Route
                path="/reservations"
                element={
                  <PrivateRoute>
                    <RoleBasedComponent
                      adminComponent={<AdminReservations />}
                      guestComponent={<GuestReservations />}
                      receptionistComponent={<ReceptionistReservations />}
                      defaultComponent={<AdminReservations />}
                    />
                  </PrivateRoute>
                }
              />
              {/* Billing - Role-based */}
              <Route
                path="/billing"
                element={
                  <PrivateRoute>
                    <RoleBasedComponent
                      adminComponent={<AdminBilling />}
                      guestComponent={<GuestBilling />}
                      defaultComponent={<AdminBilling />}
                    />
                  </PrivateRoute>
                }
              />
              {/* Housekeeping - Role-based */}
              <Route
                path="/housekeeping"
                element={
                  <PrivateRoute>
                    <RoleBasedComponent
                      adminComponent={<AdminHousekeeping />}
                      housekeepingComponent={<HousekeepingTasks />}
                      defaultComponent={<AdminHousekeeping />}
                    />
                  </PrivateRoute>
                }
              />
              {/* Maintenance - Role-based */}
              <Route
                path="/maintenance"
                element={
                  <PrivateRoute>
                    <RoleBasedComponent
                      adminComponent={<AdminMaintenance />}
                      housekeepingComponent={<HousekeepingMaintenance />}
                      defaultComponent={<AdminMaintenance />}
                    />
                  </PrivateRoute>
                }
              />
              {/* Reports - Admin/Manager only */}
              <Route
                path="/reports"
                element={
                  <PrivateRoute allowedRoles={['admin', 'manager']}>
                    <AdminReports />
                  </PrivateRoute>
                }
              />
              {/* Settings - Admin only */}
              <Route
                path="/settings"
                element={
                  <PrivateRoute allowedRoles={['admin']}>
                    <AdminSettings />
                  </PrivateRoute>
                }
              />
              {/* Gallery Management - Admin/Manager */}
              <Route
                path="/admin/gallery"
                element={
                  <PrivateRoute allowedRoles={['admin', 'manager']}>
                    <AdminGallery />
                  </PrivateRoute>
                }
              />
              {/* Services - Guest */}
              <Route
                path="/services"
                element={
                  <PrivateRoute>
                    <GuestServices />
                  </PrivateRoute>
                }
              />
              {/* Feedback - Guest */}
              <Route
                path="/feedback"
                element={
                  <PrivateRoute>
                    <GuestFeedback />
                  </PrivateRoute>
                }
              />
              {/* Gallery - Guest */}
              <Route
                path="/gallery"
                element={
                  <PrivateRoute>
                    <GuestGallery />
                  </PrivateRoute>
                }
              />
              {/* Notifications - All authenticated users */}
              <Route
                path="/notifications"
                element={
                  <PrivateRoute>
                    <Notifications />
                  </PrivateRoute>
                }
              />
          <Route path="/" element={<Home />} />
    </Routes>
  );
};

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Navbar />
          <div className="container">
            <AppRoutes />
          </div>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
