import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const Maintenance = () => {
  const { user } = useContext(AuthContext);
  const [requests, setRequests] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    roomId: '',
    issueType: 'other',
    description: '',
    priority: 'medium'
  });
  const [filter, setFilter] = useState({ status: '', issueType: '' });

  useEffect(() => {
    fetchRequests();
    fetchRooms();
  }, [filter]);

  const fetchRequests = async () => {
    try {
      const params = {};
      if (filter.status) params.status = filter.status;
      if (filter.issueType) params.issueType = filter.issueType;
      
      const response = await axios.get('/api/maintenance', { params });
      setRequests(response.data.requests);
    } catch (error) {
      console.error('Error fetching requests:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchRooms = async () => {
    try {
      const response = await axios.get('/api/rooms');
      setRooms(response.data.rooms);
    } catch (error) {
      console.error('Error fetching rooms:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/maintenance', formData);
      setShowForm(false);
      setFormData({
        roomId: '',
        issueType: 'other',
        description: '',
        priority: 'medium'
      });
      fetchRequests();
    } catch (error) {
      alert(error.response?.data?.message || 'Error creating request');
    }
  };

  const handleStatusUpdate = async (id, status) => {
    try {
      await axios.put(`/api/maintenance/${id}`, { status });
      fetchRequests();
    } catch (error) {
      alert(error.response?.data?.message || 'Error updating request');
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h1>Maintenance Requests</h1>
        <button className="btn btn-primary" onClick={() => setShowForm(!showForm)}>
          {showForm ? 'Cancel' : 'New Request'}
        </button>
      </div>

      {showForm && (
        <div className="card">
          <h2>Create Maintenance Request</h2>
          <form onSubmit={handleSubmit}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
              <div className="form-group">
                <label>Room</label>
                <select
                  value={formData.roomId}
                  onChange={(e) => setFormData({ ...formData, roomId: e.target.value })}
                  required
                >
                  <option value="">Select Room</option>
                  {rooms.map(room => (
                    <option key={room._id} value={room._id}>
                      {room.roomNumber} - {room.roomType}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Issue Type</label>
                <select
                  value={formData.issueType}
                  onChange={(e) => setFormData({ ...formData, issueType: e.target.value })}
                  required
                >
                  <option value="plumbing">Plumbing</option>
                  <option value="electrical">Electrical</option>
                  <option value="hvac">HVAC</option>
                  <option value="furniture">Furniture</option>
                  <option value="appliance">Appliance</option>
                  <option value="other">Other</option>
                </select>
              </div>
              <div className="form-group">
                <label>Priority</label>
                <select
                  value={formData.priority}
                  onChange={(e) => setFormData({ ...formData, priority: e.target.value })}
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="urgent">Urgent</option>
                </select>
              </div>
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                required
                placeholder="Describe the maintenance issue..."
              />
            </div>
            <button type="submit" className="btn btn-primary">Submit Request</button>
          </form>
        </div>
      )}

      <div className="card">
        <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
          <select
            value={filter.status}
            onChange={(e) => setFilter({ ...filter, status: e.target.value })}
            style={{ padding: '8px' }}
          >
            <option value="">All Status</option>
            <option value="reported">Reported</option>
            <option value="assigned">Assigned</option>
            <option value="in-progress">In Progress</option>
            <option value="resolved">Resolved</option>
            <option value="cancelled">Cancelled</option>
          </select>
          <select
            value={filter.issueType}
            onChange={(e) => setFilter({ ...filter, issueType: e.target.value })}
            style={{ padding: '8px' }}
          >
            <option value="">All Types</option>
            <option value="plumbing">Plumbing</option>
            <option value="electrical">Electrical</option>
            <option value="hvac">HVAC</option>
            <option value="furniture">Furniture</option>
            <option value="appliance">Appliance</option>
            <option value="other">Other</option>
          </select>
        </div>

        <table className="table">
          <thead>
            <tr>
              <th>Room</th>
              <th>Issue Type</th>
              <th>Description</th>
              <th>Priority</th>
              <th>Reported By</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map(request => (
              <tr key={request._id}>
                <td>{request.roomId?.roomNumber} - {request.roomId?.roomType}</td>
                <td>{request.issueType}</td>
                <td>{request.description}</td>
                <td>
                  <span className={`badge ${
                    request.priority === 'urgent' ? 'badge-danger' :
                    request.priority === 'high' ? 'badge-warning' :
                    'badge-info'
                  }`}>
                    {request.priority}
                  </span>
                </td>
                <td>{request.reportedBy?.firstName} {request.reportedBy?.lastName}</td>
                <td>
                  <span className={`badge ${
                    request.status === 'resolved' ? 'badge-success' :
                    request.status === 'in-progress' ? 'badge-info' :
                    request.status === 'cancelled' ? 'badge-danger' :
                    'badge-warning'
                  }`}>
                    {request.status}
                  </span>
                </td>
                <td>
                  <select
                    value={request.status}
                    onChange={(e) => handleStatusUpdate(request._id, e.target.value)}
                    style={{ padding: '5px' }}
                  >
                    <option value="reported">Reported</option>
                    <option value="assigned">Assigned</option>
                    <option value="in-progress">In Progress</option>
                    <option value="resolved">Resolved</option>
                    <option value="cancelled">Cancelled</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Maintenance;
