import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import './GuestDashboard.css'; // Import the CSS file

const GuestDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myReservations, setMyReservations] = useState([]);
  const [activeReservations, setActiveReservations] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGuestStats();
  }, []);

  const fetchGuestStats = async () => {
    try {
      const response = await axios.get('/api/reservations');
      const reservations = response.data.reservations || [];
      setMyReservations(reservations);
      setActiveReservations(reservations.filter(r => ['confirmed', 'checked-in'].includes(r.status)).length);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner-wrapper">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const getStatusClass = (status) => {
    switch(status) {
      case 'confirmed': return 'confirmed';
      case 'checked-in': return 'checked-in';
      case 'checked-out': return 'checked-out';
      case 'cancelled': return 'cancelled';
      default: return 'pending';
    }
  };

  return (
    <div className="guest-dashboard-container">
      <div style={{ maxWidth: '1400px', margin: '0 auto', position: 'relative', zIndex: 1 }}>
        {/* Header */}
        <div className="glass-card" style={{ marginBottom: '2rem' }}>
          <div className="dashboard-header">
            <div>
              <h1 className="header-title">Guest Portal</h1>
              <p className="header-subtitle">
                Welcome back, <span className="user-name">{user?.firstName}</span>! Your luxury experience awaits
              </p>
            </div>
            <button onClick={handleLogout} className="logout-btn">
              <span>üö™</span>
              Logout
            </button>
          </div>
        </div>

        {/* Stats Cards */}
        <div className="stats-grid">
          <div className="stat-card blue">
            <div className="stat-content">
              <div>
                <h3 className="stat-label">Total Reservations</h3>
                <p className="stat-value">{myReservations.length}</p>
              </div>
              <div className="stat-icon blue">
                <span style={{ fontSize: '2.5rem' }}>üìÖ</span>
              </div>
            </div>
            <div className="stat-progress">
              <div className="stat-progress-bar blue" style={{ width: '75%' }}></div>
            </div>
          </div>

          <div className="stat-card green">
            <div className="stat-content">
              <div>
                <h3 className="stat-label">Active Bookings</h3>
                <p className="stat-value green-gradient">{activeReservations}</p>
              </div>
              <div className="stat-icon green">
                <span style={{ fontSize: '2.5rem' }}>üõèÔ∏è</span>
              </div>
            </div>
            <div className="stat-progress">
              <div className="stat-progress-bar green" style={{ width: '100%' }}></div>
            </div>
          </div>
        </div>

        {/* Reservations Table */}
        <div className="glass-card" style={{ marginBottom: '2rem' }}>
          <h2 className="quick-actions-title">‚ú® Recent Reservations</h2>
          
          {myReservations.length > 0 ? (
            <div style={{ overflowX: 'auto' }}>
              <table className="reservations-table">
                <thead>
                  <tr>
                    <th>Confirmation #</th>
                    <th>Room</th>
                    <th>Check-in</th>
                    <th>Check-out</th>
                    <th>Status</th>
                    <th>Amount</th>
                  </tr>
                </thead>
                <tbody>
                  {myReservations.slice(0, 5).map(reservation => (
                    <tr key={reservation._id}>
                      <td style={{ fontFamily: 'monospace' }}>{reservation.confirmationNumber}</td>
                      <td>
                        <div className="room-info">{reservation.roomId?.roomNumber}</div>
                        <div className="room-type">{reservation.roomId?.roomType}</div>
                      </td>
                      <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                      <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                      <td>
                        <span className={`status-badge ${getStatusClass(reservation.status)}`}>
                          {reservation.status}
                        </span>
                      </td>
                      <td className="amount-cell">${reservation.totalAmount?.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="empty-state">
              <span style={{ fontSize: '4rem' }}>üè®</span>
              <p className="empty-state-text">No reservations yet. Start your luxury journey today!</p>
            </div>
          )}
        </div>

        {/* Quick Actions */}
        <div className="glass-card">
          <h2 className="quick-actions-title">Quick Actions</h2>
          <div className="actions-grid">
            <a href="/reservations" className="action-button">
              <div className="action-icon blue">
                <span style={{ fontSize: '2rem' }}>üëÅÔ∏è</span>
              </div>
              <span>View Reservations</span>
            </a>
            <a href="/rooms" className="action-button">
              <div className="action-icon purple">
                <span style={{ fontSize: '2rem' }}>üõèÔ∏è</span>
              </div>
              <span>Browse Rooms</span>
            </a>
            <a href="/services" className="action-button">
              <div className="action-icon pink">
                <span style={{ fontSize: '2rem' }}>‚ú®</span>
              </div>
              <span>Request Service</span>
            </a>
            <a href="/feedback" className="action-button">
              <div className="action-icon amber">
                <span style={{ fontSize: '2rem' }}>üìù</span>
              </div>
              <span>Leave Feedback</span>
            </a>
            <a href="/billing" className="action-button">
              <div className="action-icon green">
                <span style={{ fontSize: '2rem' }}>üí≥</span>
              </div>
              <span>View Bills</span>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GuestDashboard;
