import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import AdminLayout from '../../components/AdminLayout.jsx';

const ReceptionistDashboard = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalRooms: 0,
    availableRooms: 0,
    occupiedRooms: 0,
    cleaningRooms: 0,
    pendingReservations: 0,
    checkedInGuests: 0
  });
  const [loading, setLoading] = useState(true);
  const [todayReservations, setTodayReservations] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch today's date
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      const tomorrow = new Date(today);
      tomorrow.setDate(tomorrow.getDate() + 1);

      // Fetch reservations for today
      const reservationsResponse = await axios.get('/api/reservations');
      const allReservations = reservationsResponse.data.reservations || [];
      
      // Filter today's check-ins and check-outs
      const todayCheckIns = allReservations.filter(r => {
        const checkInDate = new Date(r.checkInDate);
        checkInDate.setHours(0, 0, 0, 0);
        return checkInDate.getTime() === today.getTime() && r.status === 'confirmed';
      });

      const todayCheckOuts = allReservations.filter(r => {
        const checkOutDate = new Date(r.checkOutDate);
        checkOutDate.setHours(0, 0, 0, 0);
        return checkOutDate.getTime() === today.getTime() && r.status === 'checked-in';
      });

      const checkedInGuests = allReservations.filter(r => r.status === 'checked-in').length;
      const pendingReservations = allReservations.filter(r => r.status === 'pending' || r.status === 'confirmed').length;

      // Fetch rooms
      const roomsResponse = await axios.get('/api/rooms');
      const allRooms = roomsResponse.data.rooms || [];
      
      const availableRooms = allRooms.filter(r => r.status === 'available').length;
      const occupiedRooms = allRooms.filter(r => r.status === 'occupied').length;
      const cleaningRooms = allRooms.filter(r => r.status === 'cleaning').length;

      // Get today's reservations (check-ins and check-outs)
      const todayRes = allReservations.filter(r => {
        const checkIn = new Date(r.checkInDate);
        checkIn.setHours(0, 0, 0, 0);
        const checkOut = new Date(r.checkOutDate);
        checkOut.setHours(0, 0, 0, 0);
        return (checkIn.getTime() === today.getTime() || checkOut.getTime() === today.getTime()) &&
               ['confirmed', 'checked-in'].includes(r.status);
      }).slice(0, 5);

      setStats({
        todayCheckIns: todayCheckIns.length,
        todayCheckOuts: todayCheckOuts.length,
        totalRooms: allRooms.length,
        availableRooms,
        occupiedRooms,
        cleaningRooms,
        pendingReservations,
        checkedInGuests
      });

      setTodayReservations(todayRes);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  const occupancyRate = stats.totalRooms > 0 ? Math.round((stats.occupiedRooms / stats.totalRooms) * 100) : 0;

  return (
    <AdminLayout 
      title="Receptionist Dashboard" 
      subtitle={`Welcome, ${user?.firstName}! Manage check-ins, check-outs, and room status`}
    >
      {/* Top Statistics Cards - Luxury Gold & Navy Theme */}
      <div className="grid grid-4 mb-3">
        <div className="card" style={{ 
          background: 'linear-gradient(135deg, #D4AF37 0%, #C9A961 100%)',
          color: '#1a2332',
          border: 'none',
          boxShadow: '0 8px 24px rgba(212, 175, 55, 0.3)'
        }}>
          <h3 style={{ color: '#1a2332', marginBottom: '12px', fontSize: '14px', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
            Today's Check-ins
          </h3>
          <p style={{ fontSize: '42px', fontWeight: 'bold', margin: '0', color: '#1a2332' }}>
            {stats.todayCheckIns}
          </p>
          <p style={{ marginTop: '8px', color: '#2c3e50', fontSize: '12px' }}>Reservations scheduled</p>
        </div>

        <div className="card" style={{ 
          background: 'linear-gradient(135deg, #1a2332 0%, #2c3e50 100%)',
          color: 'white',
          border: 'none',
          boxShadow: '0 8px 24px rgba(26, 35, 50, 0.3)'
        }}>
          <h3 style={{ color: 'white', marginBottom: '12px', fontSize: '14px', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
            Today's Check-outs
          </h3>
          <p style={{ fontSize: '42px', fontWeight: 'bold', margin: '0', color: 'white', textShadow: '0 2px 8px rgba(0,0,0,0.2)' }}>
            {stats.todayCheckOuts}
          </p>
          <p style={{ marginTop: '8px', opacity: 0.95, fontSize: '12px' }}>Guests leaving today</p>
        </div>

        <div className="card" style={{ 
          background: 'linear-gradient(135deg, #E5C158 0%, #D4AF37 100%)',
          color: '#1a2332',
          border: 'none',
          boxShadow: '0 8px 24px rgba(212, 175, 55, 0.25)'
        }}>
          <h3 style={{ color: '#1a2332', marginBottom: '12px', fontSize: '14px', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
            Checked-in Guests
          </h3>
          <p style={{ fontSize: '42px', fontWeight: 'bold', margin: '0', color: '#1a2332' }}>
            {stats.checkedInGuests}
          </p>
          <p style={{ marginTop: '8px', color: '#2c3e50', fontSize: '12px' }}>Currently in hotel</p>
        </div>

        <div className="card" style={{ 
          background: 'linear-gradient(135deg, #4ECDC4 0%, #45B7B8 100%)',
          color: 'white',
          border: 'none',
          boxShadow: '0 8px 24px rgba(78, 205, 196, 0.3)'
        }}>
          <h3 style={{ color: 'white', marginBottom: '12px', fontSize: '14px', fontWeight: '600', textTransform: 'uppercase', letterSpacing: '0.5px' }}>
            Available Rooms
          </h3>
          <p style={{ fontSize: '42px', fontWeight: 'bold', margin: '0', color: 'white', textShadow: '0 2px 8px rgba(0,0,0,0.2)' }}>
            {stats.availableRooms}
          </p>
          <p style={{ marginTop: '8px', opacity: 0.95, fontSize: '12px' }}>Out of {stats.totalRooms} total</p>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="admin-dashboard-grid">
        {/* Today's Reservations Table */}
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <div>
              <h2 style={{ color: '#1a2332', marginBottom: '4px' }}>Today's Reservations</h2>
              <p style={{ fontSize: '12px', color: '#6c757d' }}>Check-ins and check-outs scheduled for today</p>
            </div>
            <button 
              className="btn btn-primary" 
              onClick={() => navigate('/reservations')}
              style={{ padding: '10px 20px', fontSize: '14px' }}
            >
              View All
            </button>
          </div>
          {todayReservations.length > 0 ? (
            <table className="table">
              <thead>
                <tr>
                  <th>Confirmation #</th>
                  <th>Guest Name</th>
                  <th>Room</th>
                  <th>Check-in</th>
                  <th>Check-out</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {todayReservations.map(reservation => (
                  <tr key={reservation._id}>
                    <td style={{ fontWeight: '600', color: '#1a2332' }}>{reservation.confirmationNumber}</td>
                    <td>{reservation.guestId?.firstName} {reservation.guestId?.lastName}</td>
                    <td>{reservation.roomId?.roomNumber} - {reservation.roomId?.roomType}</td>
                    <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                    <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                    <td>
                      <span className={`badge ${
                        reservation.status === 'confirmed' ? 'badge-success' :
                        reservation.status === 'checked-in' ? 'badge-info' :
                        'badge-warning'
                      }`}>
                        {reservation.status}
                      </span>
                    </td>
                    <td>
                      {reservation.status === 'confirmed' && (
                        <button
                          className="btn btn-success"
                          onClick={() => navigate(`/reservations`)}
                          style={{ padding: '6px 14px', fontSize: '12px' }}
                        >
                          Check-in
                        </button>
                      )}
                      {reservation.status === 'checked-in' && (
                        <button
                          className="btn btn-primary"
                          onClick={() => navigate(`/reservations`)}
                          style={{ padding: '6px 14px', fontSize: '12px' }}
                        >
                          Check-out
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <div style={{ textAlign: 'center', padding: '40px 20px', color: '#6c757d' }}>
              <p style={{ fontSize: '16px', marginBottom: '8px' }}>No reservations scheduled for today</p>
              <p style={{ fontSize: '12px', opacity: 0.8 }}>All clear! No check-ins or check-outs today.</p>
            </div>
          )}
        </div>

        {/* Side Widgets */}
        <div style={{ display: 'flex', flexDirection: 'column', gap: 18 }}>
          {/* Room Status Summary */}
          <div className="card">
            <h3 style={{ color: '#1a2332', marginBottom: '16px' }}>Room Status</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '12px', background: '#f8f9fa', borderRadius: '8px' }}>
                <div>
                  <span style={{ fontSize: '13px', color: '#6c757d' }}>Occupied</span>
                  <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#1a2332', margin: '4px 0 0 0' }}>
                    {stats.occupiedRooms}
                  </p>
                </div>
                <div style={{ 
                  width: '60px', 
                  height: '60px', 
                  borderRadius: '50%', 
                  background: `conic-gradient(#D4AF37 0deg, #D4AF37 ${occupancyRate * 3.6}deg, #e9ecef ${occupancyRate * 3.6}deg)`,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  boxShadow: '0 4px 12px rgba(212, 175, 55, 0.2)'
                }}>
                  <div style={{ 
                    width: '44px', 
                    height: '44px', 
                    borderRadius: '50%', 
                    background: 'white',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontWeight: 'bold',
                    color: '#1a2332',
                    fontSize: '12px'
                  }}>
                    {occupancyRate}%
                  </div>
                </div>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderTop: '1px solid #e9ecef' }}>
                <span style={{ fontSize: '13px', color: '#6c757d' }}>Available</span>
                <span style={{ fontSize: '16px', fontWeight: '600', color: '#28a745' }}>{stats.availableRooms}</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderTop: '1px solid #e9ecef' }}>
                <span style={{ fontSize: '13px', color: '#6c757d' }}>Cleaning</span>
                <span style={{ fontSize: '16px', fontWeight: '600', color: '#ffc107' }}>{stats.cleaningRooms}</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0', borderTop: '1px solid #e9ecef' }}>
                <span style={{ fontSize: '13px', color: '#6c757d' }}>Total Rooms</span>
                <span style={{ fontSize: '16px', fontWeight: '600', color: '#1a2332' }}>{stats.totalRooms}</span>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="card">
            <h3 style={{ color: '#1a2332', marginBottom: '16px' }}>Quick Actions</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
              <button 
                className="btn btn-primary" 
                onClick={() => navigate('/reservations')}
                style={{ width: '100%', justifyContent: 'center' }}
              >
                Manage Reservations
              </button>
              <button 
                className="btn btn-primary" 
                onClick={() => navigate('/rooms')}
                style={{ width: '100%', justifyContent: 'center' }}
              >
                View Rooms
              </button>
              <button 
                className="btn btn-success" 
                onClick={() => navigate('/reservations?status=confirmed')}
                style={{ width: '100%', justifyContent: 'center' }}
              >
                Pending Check-ins
              </button>
              <button 
                className="btn btn-secondary" 
                onClick={() => navigate('/reservations?status=checked-in')}
                style={{ width: '100%', justifyContent: 'center' }}
              >
                Active Guests
              </button>
            </div>
          </div>

          {/* Additional Stats */}
          <div className="card">
            <h3 style={{ color: '#1a2332', marginBottom: '16px' }}>Summary</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px', background: 'linear-gradient(135deg, rgba(212, 175, 55, 0.1) 0%, rgba(212, 175, 55, 0.05) 100%)', borderRadius: '8px' }}>
                <span style={{ fontSize: '13px', color: '#6c757d' }}>Pending Reservations</span>
                <span className="badge badge-warning" style={{ fontSize: '13px', padding: '6px 12px' }}>
                  {stats.pendingReservations}
                </span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '10px', background: 'linear-gradient(135deg, rgba(26, 35, 50, 0.05) 0%, rgba(44, 62, 80, 0.05) 100%)', borderRadius: '8px' }}>
                <span style={{ fontSize: '13px', color: '#6c757d' }}>Checked-in Guests</span>
                <span className="badge badge-info" style={{ fontSize: '13px', padding: '6px 12px' }}>
                  {stats.checkedInGuests}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </AdminLayout>
  );
};

export default ReceptionistDashboard;
