import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import GuestFooter from '../../components/GuestFooter.jsx';

const RoomDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [numberOfGuests, setNumberOfGuests] = useState(1);

  useEffect(() => {
    fetchRoomDetails();
  }, [id]);

  const fetchRoomDetails = async () => {
    try {
      const response = await axios.get(`/api/rooms/${id}`);
      setRoom(response.data.room);
    } catch (error) {
      console.error('Error fetching room details:', error);
      alert('Room not found');
      navigate('/rooms');
    } finally {
      setLoading(false);
    }
  };

  const handleBookNow = () => {
    if (!checkInDate || !checkOutDate) {
      alert('Please select check-in and check-out dates');
      return;
    }
    
    const checkIn = new Date(checkInDate);
    const checkOut = new Date(checkOutDate);
    
    if (checkOut <= checkIn) {
      alert('Check-out date must be after check-in date');
      return;
    }

    // Navigate to reservations page with room data
    navigate('/reservations', {
      state: {
        roomId: room._id,
        checkInDate,
        checkOutDate,
        numberOfGuests
      }
    });
  };

  if (loading) {
    return (
      <div className="guest-page-container">
        <div className="loading"><div className="spinner"></div></div>
      </div>
    );
  }

  if (!room) {
    return (
      <div className="guest-page-container">
        <div className="empty-state">
          <p>Room not found</p>
          <Link to="/rooms" className="btn btn-primary">Back to Rooms</Link>
        </div>
      </div>
    );
  }

  const nights = checkInDate && checkOutDate 
    ? Math.ceil((new Date(checkOutDate) - new Date(checkInDate)) / (1000 * 60 * 60 * 24))
    : 1;
  const totalPrice = room.pricePerNight * nights;

  return (
    <div className="guest-page-container">
      {/* Breadcrumb */}
      <div style={{ 
        padding: '20px 0', 
        borderBottom: '1px solid #e0e0e0',
        marginBottom: '20px'
      }}>
        <div className="container" style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 20px' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px', fontSize: '14px', color: '#666' }}>
            <Link to="/" style={{ color: '#c7925b', textDecoration: 'none' }}>Home</Link>
            <span>/</span>
            <Link to="/rooms" style={{ color: '#c7925b', textDecoration: 'none' }}>Rooms</Link>
            <span>/</span>
            <span style={{ color: '#333' }}>Room {room.roomNumber}</span>
          </div>
        </div>
      </div>

      <div className="container" style={{ maxWidth: '1200px', margin: '0 auto', padding: '0 20px' }}>
        {/* Room Header */}
        <div style={{ marginBottom: '30px' }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '10px' }}>
            <div>
              <h1 style={{ 
                fontSize: '36px', 
                fontWeight: 'bold', 
                color: '#333', 
                margin: 0,
                marginBottom: '8px'
              }}>
                Room {room.roomNumber}
              </h1>
              <p style={{ fontSize: '18px', color: '#666', margin: 0 }}>
                {room.roomType} ‚Ä¢ Floor {room.floor}
              </p>
            </div>
            <div style={{ textAlign: 'right' }}>
              <div style={{ fontSize: '32px', fontWeight: 'bold', color: '#c7925b' }}>
                ${room.pricePerNight.toFixed(2)}
              </div>
              <div style={{ fontSize: '14px', color: '#999' }}>per night</div>
            </div>
          </div>
        </div>

        {/* Image Gallery */}
        {room.images && room.images.length > 0 ? (
          <div style={{ marginBottom: '40px' }}>
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: room.images.length > 1 ? '2fr 1fr 1fr' : '1fr',
              gap: '10px',
              height: '500px',
              borderRadius: '12px',
              overflow: 'hidden'
            }}>
              {/* Main Image */}
              <div style={{ 
                position: 'relative',
                overflow: 'hidden',
                cursor: 'pointer'
              }}>
                <img 
                  src={room.images[selectedImageIndex]} 
                  alt={`Room ${room.roomNumber} - Image ${selectedImageIndex + 1}`}
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    transition: 'transform 0.3s ease'
                  }}
                />
                {room.images.length > 1 && (
                  <div style={{
                    position: 'absolute',
                    bottom: '20px',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    display: 'flex',
                    gap: '8px'
                  }}>
                    {room.images.map((_, index) => (
                      <button
                        key={index}
                        onClick={() => setSelectedImageIndex(index)}
                        style={{
                          width: '10px',
                          height: '10px',
                          borderRadius: '50%',
                          border: 'none',
                          background: index === selectedImageIndex ? '#fff' : 'rgba(255,255,255,0.5)',
                          cursor: 'pointer',
                          transition: 'all 0.3s ease'
                        }}
                      />
                    ))}
                  </div>
                )}
              </div>

              {/* Thumbnail Images */}
              {room.images.length > 1 && room.images.slice(0, 2).map((image, index) => (
                index + 1 < room.images.length && (
                  <div 
                    key={index + 1}
                    style={{ 
                      position: 'relative',
                      overflow: 'hidden',
                      cursor: 'pointer',
                      opacity: selectedImageIndex === index + 1 ? 1 : 0.8
                    }}
                    onClick={() => setSelectedImageIndex(index + 1)}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.opacity = '1';
                    }}
                    onMouseLeave={(e) => {
                      if (selectedImageIndex !== index + 1) {
                        e.currentTarget.style.opacity = '0.8';
                      }
                    }}
                  >
                    <img 
                      src={image} 
                      alt={`Room ${room.roomNumber} - Thumbnail ${index + 2}`}
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                    />
                  </div>
                )
              ))}
            </div>

            {/* Image Thumbnails Row (if more than 3 images) */}
            {room.images.length > 3 && (
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: `repeat(${Math.min(room.images.length - 3, 4)}, 1fr)`,
                gap: '10px',
                marginTop: '10px'
              }}>
                {room.images.slice(3).map((image, index) => (
                  <div 
                    key={index + 3}
                    style={{ 
                      height: '120px',
                      overflow: 'hidden',
                      borderRadius: '8px',
                      cursor: 'pointer',
                      opacity: selectedImageIndex === index + 3 ? 1 : 0.7
                    }}
                    onClick={() => setSelectedImageIndex(index + 3)}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.opacity = '1';
                    }}
                    onMouseLeave={(e) => {
                      if (selectedImageIndex !== index + 3) {
                        e.currentTarget.style.opacity = '0.7';
                      }
                    }}
                  >
                    <img 
                      src={image} 
                      alt={`Room ${room.roomNumber} - Thumbnail ${index + 4}`}
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        ) : (
          <div style={{ 
            height: '500px', 
            background: 'linear-gradient(135deg, #f5f5f5 0%, #e0e0e0 100%)',
            borderRadius: '12px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            marginBottom: '40px'
          }}>
            <div style={{ textAlign: 'center', color: '#999' }}>
              <div style={{ fontSize: '64px', marginBottom: '10px' }}>üõèÔ∏è</div>
              <div style={{ fontWeight: '600' }}>No Image Available</div>
            </div>
          </div>
        )}

        <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '40px', marginBottom: '40px' }}>
          {/* Left Column - Room Details */}
          <div>
            {/* Description */}
            <div style={{ marginBottom: '40px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px', color: '#333' }}>
                About this room
              </h2>
              <p style={{ fontSize: '16px', lineHeight: '1.8', color: '#666' }}>
                {room.description || 'Experience luxury and comfort in this beautifully designed room.'}
              </p>
            </div>

            {/* Amenities */}
            {room.amenities && room.amenities.length > 0 && (
              <div style={{ marginBottom: '40px' }}>
                <h2 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '16px', color: '#333' }}>
                  What this place offers
                </h2>
                <div style={{ 
                  display: 'grid', 
                  gridTemplateColumns: 'repeat(2, 1fr)', 
                  gap: '16px' 
                }}>
                  {room.amenities.map((amenity, index) => (
                    <div key={index} style={{ 
                      display: 'flex', 
                      alignItems: 'center', 
                      gap: '12px',
                      padding: '12px',
                      background: '#f9f9f9',
                      borderRadius: '8px'
                    }}>
                      <span style={{ fontSize: '20px' }}>‚úì</span>
                      <span style={{ fontSize: '16px', color: '#333' }}>{amenity}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Room Info */}
            <div style={{ 
              padding: '24px', 
              background: '#f9f9f9', 
              borderRadius: '12px',
              marginBottom: '40px'
            }}>
              <h3 style={{ fontSize: '20px', fontWeight: 'bold', marginBottom: '20px', color: '#333' }}>
                Room Information
              </h3>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', gap: '20px' }}>
                <div>
                  <div style={{ fontSize: '14px', color: '#999', marginBottom: '4px' }}>Room Number</div>
                  <div style={{ fontSize: '18px', fontWeight: '600', color: '#333' }}>{room.roomNumber}</div>
                </div>
                <div>
                  <div style={{ fontSize: '14px', color: '#999', marginBottom: '4px' }}>Floor</div>
                  <div style={{ fontSize: '18px', fontWeight: '600', color: '#333' }}>Floor {room.floor}</div>
                </div>
                <div>
                  <div style={{ fontSize: '14px', color: '#999', marginBottom: '4px' }}>Room Type</div>
                  <div style={{ fontSize: '18px', fontWeight: '600', color: '#333' }}>{room.roomType}</div>
                </div>
                <div>
                  <div style={{ fontSize: '14px', color: '#999', marginBottom: '4px' }}>Max Occupancy</div>
                  <div style={{ fontSize: '18px', fontWeight: '600', color: '#333' }}>{room.maxOccupancy} Guests</div>
                </div>
              </div>
            </div>
          </div>

          {/* Right Column - Booking Card */}
          <div>
            <div style={{
              position: 'sticky',
              top: '20px',
              background: '#fff',
              border: '2px solid #e0e0e0',
              borderRadius: '16px',
              padding: '24px',
              boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
            }}>
              <div style={{ marginBottom: '24px' }}>
                <div style={{ fontSize: '32px', fontWeight: 'bold', color: '#c7925b', marginBottom: '8px' }}>
                  ${room.pricePerNight.toFixed(2)}
                </div>
                <div style={{ fontSize: '14px', color: '#999' }}>per night</div>
              </div>

              {/* Booking Form */}
              <div style={{ marginBottom: '24px' }}>
                <div style={{ marginBottom: '16px' }}>
                  <label style={{ 
                    display: 'block', 
                    fontSize: '14px', 
                    fontWeight: '600', 
                    marginBottom: '8px',
                    color: '#333'
                  }}>
                    Check-in
                  </label>
                  <input
                    type="date"
                    value={checkInDate}
                    onChange={(e) => setCheckInDate(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #e0e0e0',
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '16px' }}>
                  <label style={{ 
                    display: 'block', 
                    fontSize: '14px', 
                    fontWeight: '600', 
                    marginBottom: '8px',
                    color: '#333'
                  }}>
                    Check-out
                  </label>
                  <input
                    type="date"
                    value={checkOutDate}
                    onChange={(e) => setCheckOutDate(e.target.value)}
                    min={checkInDate || new Date().toISOString().split('T')[0]}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #e0e0e0',
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                  />
                </div>

                <div style={{ marginBottom: '16px' }}>
                  <label style={{ 
                    display: 'block', 
                    fontSize: '14px', 
                    fontWeight: '600', 
                    marginBottom: '8px',
                    color: '#333'
                  }}>
                    Guests
                  </label>
                  <select
                    value={numberOfGuests}
                    onChange={(e) => setNumberOfGuests(parseInt(e.target.value))}
                    style={{
                      width: '100%',
                      padding: '12px',
                      border: '2px solid #e0e0e0',
                      borderRadius: '8px',
                      fontSize: '16px'
                    }}
                  >
                    {Array.from({ length: room.maxOccupancy }, (_, i) => i + 1).map(num => (
                      <option key={num} value={num}>{num} {num === 1 ? 'Guest' : 'Guests'}</option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Price Breakdown */}
              {checkInDate && checkOutDate && (
                <div style={{ 
                  padding: '16px', 
                  background: '#f9f9f9', 
                  borderRadius: '8px',
                  marginBottom: '24px'
                }}>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                    <span style={{ color: '#666' }}>${room.pricePerNight.toFixed(2)} √ó {nights} {nights === 1 ? 'night' : 'nights'}</span>
                    <span style={{ fontWeight: '600' }}>${(room.pricePerNight * nights).toFixed(2)}</span>
                  </div>
                  <div style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between', 
                    fontSize: '18px', 
                    fontWeight: 'bold',
                    paddingTop: '12px',
                    borderTop: '2px solid #e0e0e0'
                  }}>
                    <span>Total</span>
                    <span style={{ color: '#c7925b' }}>${totalPrice.toFixed(2)}</span>
                  </div>
                </div>
              )}

              {/* Book Now Button */}
              <button
                onClick={handleBookNow}
                style={{
                  width: '100%',
                  padding: '16px',
                  background: 'linear-gradient(135deg, #c7925b 0%, #8b6f47 100%)',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '18px',
                  fontWeight: '600',
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  textTransform: 'uppercase',
                  letterSpacing: '1px'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = 'linear-gradient(135deg, #8b6f47 0%, #6b5a4a 100%)';
                  e.currentTarget.style.transform = 'translateY(-2px)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = 'linear-gradient(135deg, #c7925b 0%, #8b6f47 100%)';
                  e.currentTarget.style.transform = 'translateY(0)';
                }}
              >
                Reserve Now
              </button>

              <p style={{ 
                fontSize: '12px', 
                color: '#999', 
                textAlign: 'center', 
                marginTop: '16px',
                lineHeight: '1.5'
              }}>
                You won't be charged yet
              </p>
            </div>
          </div>
        </div>
      </div>

      <GuestFooter />
    </div>
  );
};

export default RoomDetails;
