import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const GuestDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myReservations, setMyReservations] = useState([]);
  const [activeReservations, setActiveReservations] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchGuestStats();
  }, []);

  const fetchGuestStats = async () => {
    try {
      const response = await axios.get('/api/reservations');
      const reservations = response.data.reservations || [];
      setMyReservations(reservations);
      setActiveReservations(reservations.filter(r => ['confirmed', 'checked-in'].includes(r.status)).length);
    } catch (error) {
      console.error('Error fetching reservations:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
          <h1>Guest Dashboard</h1>
          <p style={{ color: '#666', marginTop: '5px' }}>Welcome, {user?.firstName}! Manage your bookings and services</p>
        </div>
        <button onClick={handleLogout} className="btn btn-danger" style={{ marginLeft: 'auto' }}>
          Logout
        </button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
        <div className="card">
          <h3>My Reservations</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#007bff' }}>{myReservations.length}</p>
        </div>
        <div className="card">
          <h3>Active Bookings</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#28a745' }}>{activeReservations}</p>
        </div>
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h2>My Recent Reservations</h2>
        {myReservations.length > 0 ? (
          <table className="table" style={{ marginTop: '15px' }}>
            <thead>
              <tr>
                <th>Confirmation #</th>
                <th>Room</th>
                <th>Check-in</th>
                <th>Check-out</th>
                <th>Status</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              {myReservations.slice(0, 5).map(reservation => (
                <tr key={reservation._id}>
                  <td>{reservation.confirmationNumber}</td>
                  <td>{reservation.roomId?.roomNumber} - {reservation.roomId?.roomType}</td>
                  <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                  <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                  <td>
                    <span className={`badge ${
                      reservation.status === 'confirmed' ? 'badge-success' :
                      reservation.status === 'checked-in' ? 'badge-info' :
                      reservation.status === 'checked-out' ? 'badge-secondary' :
                      reservation.status === 'cancelled' ? 'badge-danger' :
                      'badge-warning'
                    }`}>
                      {reservation.status}
                    </span>
                  </td>
                  <td>${reservation.totalAmount?.toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ marginTop: '15px', color: '#666' }}>No reservations yet</p>
        )}
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h2>Quick Actions</h2>
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap', marginTop: '20px' }}>
          <a href="/reservations" className="btn btn-primary">View Reservations</a>
          <a href="/rooms" className="btn btn-primary">Browse Rooms</a>
          <a href="/services" className="btn btn-primary">Request Service</a>
          <a href="/feedback" className="btn btn-primary">Leave Feedback</a>
          <a href="/billing" className="btn btn-primary">View Bills</a>
        </div>
      </div>
    </div>
  );
};

export default GuestDashboard;
