import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import GuestFooter from '../../components/GuestFooter.jsx';
import Chat from '../../components/Chat.jsx';
import NotificationBell from '../../components/NotificationBell.jsx';
import '../guest/GuestDashboard.css';

const ReceptionistDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [currentSlide, setCurrentSlide] = useState(0);
  const [chatOpen, setChatOpen] = useState(false);
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalRooms: 0,
    availableRooms: 0,
    occupiedRooms: 0,
    cleaningRooms: 0,
    pendingReservations: 0,
    checkedInGuests: 0
  });
  const [loading, setLoading] = useState(true);
  const [todayReservations, setTodayReservations] = useState([]);

  const carouselImages = [
    'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=1920&q=80',
    'https://images.unsplash.com/photo-1631049307264-da0ec9d70304?w=1920&q=80',
    'https://images.unsplash.com/photo-1571896349842-33c89424de2d?w=1920&q=80'
  ];

  // Auto carousel effect
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % carouselImages.length);
    }, 4000);

    return () => clearInterval(interval);
  }, [carouselImages.length]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const reservationsResponse = await axios.get('/api/reservations');
      const allReservations = reservationsResponse.data.reservations || [];

      const todayCheckIns = allReservations.filter(r => {
        const checkInDate = new Date(r.checkInDate);
        checkInDate.setHours(0, 0, 0, 0);
        return checkInDate.getTime() === today.getTime() && r.status === 'confirmed';
      });

      const todayCheckOuts = allReservations.filter(r => {
        const checkOutDate = new Date(r.checkOutDate);
        checkOutDate.setHours(0, 0, 0, 0);
        return checkOutDate.getTime() === today.getTime() && r.status === 'checked-in';
      });

      const checkedInGuests = allReservations.filter(r => r.status === 'checked-in').length;
      const pendingReservations = allReservations.filter(r => r.status === 'pending' || r.status === 'confirmed').length;

      const roomsResponse = await axios.get('/api/rooms');
      const allRooms = roomsResponse.data.rooms || [];

      const availableRooms = allRooms.filter(r => r.status === 'available').length;
      const occupiedRooms = allRooms.filter(r => r.status === 'occupied').length;
      const cleaningRooms = allRooms.filter(r => r.status === 'cleaning').length;

      const todayRes = allReservations.filter(r => {
        const checkIn = new Date(r.checkInDate);
        checkIn.setHours(0, 0, 0, 0);
        const checkOut = new Date(r.checkOutDate);
        checkOut.setHours(0, 0, 0, 0);
        return (checkIn.getTime() === today.getTime() || checkOut.getTime() === today.getTime()) &&
               ['confirmed', 'checked-in'].includes(r.status);
      }).slice(0, 5);

      setStats({
        todayCheckIns: todayCheckIns.length,
        todayCheckOuts: todayCheckOuts.length,
        totalRooms: allRooms.length,
        availableRooms,
        occupiedRooms,
        cleaningRooms,
        pendingReservations,
        checkedInGuests
      });

      setTodayReservations(todayRes);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div className="guest-dashboard-container">
      {/* Image Carousel */}
      <div className="dashboard-carousel">
        <div 
          className="carousel-wrapper"
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {carouselImages.map((image, index) => (
            <div key={index} className="carousel-slide">
              <img src={image} alt={`Luxury Stay ${index + 1}`} className="carousel-image" />
              <div className="carousel-overlay"></div>
            </div>
          ))}
        </div>

        {/* Carousel Indicators */}
        <div className="carousel-indicators">
          {carouselImages.map((_, index) => (
            <button
              key={index}
              className={`carousel-indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
              aria-label={`Go to slide ${index + 1}`}
            />
          ))}
        </div>
      </div>

      {/* Welcome Banner */}
      <div className="dashboard-content-wrapper">
        <div className="welcome-banner-card">
          <div className="welcome-banner-content">
            <div className="welcome-greeting">
              <h2 className="welcome-title">Welcome back, {user?.firstName || 'Receptionist'}!</h2>
              <p className="welcome-message">Manage check-ins, check-outs, and room status efficiently.</p>
            </div>
            <div className="welcome-stats">
              <div className="welcome-stat-item">
                <div className="stat-icon">
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                    <polyline points="22 4 12 14.01 9 11.01"></polyline>
                  </svg>
                </div>
                <div className="stat-info">
                  <div className="stat-value">
                    {loading ? (
                      <span className="stat-loading">...</span>
                    ) : (
                      stats.todayCheckIns
                    )}
                  </div>
                  <div className="stat-label">Today's Check-ins</div>
                </div>
              </div>
              <div className="welcome-stat-item">
                <div className="stat-icon">
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
                    <circle cx="12" cy="12" r="3"></circle>
                  </svg>
                </div>
                <div className="stat-info">
                  <div className="stat-value">
                    {loading ? (
                      <span className="stat-loading">...</span>
                    ) : (
                      stats.todayCheckOuts
                    )}
                  </div>
                  <div className="stat-label">Today's Check-outs</div>
                </div>
              </div>
              <div className="welcome-stat-item">
                <div className="stat-icon">
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                    <circle cx="9" cy="7" r="4"></circle>
                    <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
                    <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
                  </svg>
                </div>
                <div className="stat-info">
                  <div className="stat-value">
                    {loading ? (
                      <span className="stat-loading">...</span>
                    ) : (
                      stats.checkedInGuests
                    )}
                  </div>
                  <div className="stat-label">Checked-in Guests</div>
                </div>
              </div>
              <div className="welcome-stat-item">
                <div className="stat-icon">
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
                  </svg>
                </div>
                <div className="stat-info">
                  <div className="stat-value">
                    {loading ? (
                      <span className="stat-loading">...</span>
                    ) : (
                      stats.availableRooms
                    )}
                  </div>
                  <div className="stat-label">Available Rooms</div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Decorative Separator */}
      <div className="section-separator">
        <div className="separator-line"></div>
        <div className="separator-icon">
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
          </svg>
        </div>
        <div className="separator-line"></div>
      </div>

      {/* Statistics Cards */}
      <div className="dashboard-content-wrapper">
        <div className="page-stats-grid">
          <div className="page-stat-card">
            <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                <polyline points="22 4 12 14.01 9 11.01"></polyline>
              </svg>
            </div>
            <div className="stat-card-content">
              <div className="stat-card-value">{stats.todayCheckIns}</div>
              <div className="stat-card-label">Today's Check-ins</div>
            </div>
          </div>

          <div className="page-stat-card">
            <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)' }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z"></path>
                <circle cx="12" cy="12" r="3"></circle>
              </svg>
            </div>
            <div className="stat-card-content">
              <div className="stat-card-value">{stats.todayCheckOuts}</div>
              <div className="stat-card-label">Today's Check-outs</div>
            </div>
          </div>

          <div className="page-stat-card">
            <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)' }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                <circle cx="9" cy="7" r="4"></circle>
                <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
                <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
              </svg>
            </div>
            <div className="stat-card-content">
              <div className="stat-card-value">{stats.checkedInGuests}</div>
              <div className="stat-card-label">Checked-in Guests</div>
            </div>
          </div>

          <div className="page-stat-card">
            <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)' }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
              </svg>
            </div>
            <div className="stat-card-content">
              <div className="stat-card-value">{stats.availableRooms}</div>
              <div className="stat-card-label">Available Rooms</div>
            </div>
          </div>

          <div className="page-stat-card">
            <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)' }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
                <polyline points="9 22 9 12 15 12 15 22"></polyline>
              </svg>
            </div>
            <div className="stat-card-content">
              <div className="stat-card-value">{stats.occupiedRooms}</div>
              <div className="stat-card-label">Occupied Rooms</div>
            </div>
          </div>

          <div className="page-stat-card">
            <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)' }}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M14.5 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V7.5L14.5 2z"></path>
                <polyline points="14 2 14 8 20 8"></polyline>
                <line x1="16" y1="13" x2="8" y2="13"></line>
                <line x1="16" y1="17" x2="8" y2="17"></line>
              </svg>
            </div>
            <div className="stat-card-content">
              <div className="stat-card-value">{stats.cleaningRooms}</div>
              <div className="stat-card-label">Rooms Cleaning</div>
            </div>
          </div>
        </div>
      </div>

      {/* Today's Reservations */}
      <div className="dashboard-content-wrapper">
        <div className="glass-card">
          <h2 className="section-title">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
            </svg>
            <span>Today's Reservations</span>
          </h2>

          <div style={{ display: 'flex', justifyContent: 'flex-end', marginBottom: '1rem' }}>
            <button className="btn btn-primary" onClick={() => navigate('/reservations')}>
              View All Reservations
            </button>
          </div>

          {todayReservations.length > 0 ? (
            <div style={{ overflowX: 'auto' }}>
              <table className="table">
                <thead>
                  <tr>
                    <th>Confirmation #</th>
                    <th>Guest Name</th>
                    <th>Room</th>
                    <th>Check-in</th>
                    <th>Check-out</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {todayReservations.map(reservation => (
                    <tr key={reservation._id}>
                      <td>{reservation.confirmationNumber}</td>
                      <td>{reservation.guestId?.firstName} {reservation.guestId?.lastName}</td>
                      <td>{reservation.roomId?.roomNumber} - {reservation.roomId?.roomType}</td>
                      <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                      <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                      <td>
                        <span className={`badge ${
                          reservation.status === 'confirmed' ? 'badge-success' :
                          reservation.status === 'checked-in' ? 'badge-info' :
                          'badge-warning'
                        }`}>
                          {reservation.status}
                        </span>
                      </td>
                      <td>
                        {reservation.status === 'confirmed' && (
                          <button
                            className="btn btn-success"
                            onClick={() => navigate(`/reservations`)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                          >
                            Check-in
                          </button>
                        )}
                        {reservation.status === 'checked-in' && (
                          <button
                            className="btn btn-primary"
                            onClick={() => navigate(`/reservations`)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                          >
                            Check-out
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-state-icon">
                <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                  <polyline points="14 2 14 8 20 8"></polyline>
                  <line x1="16" y1="13" x2="8" y2="13"></line>
                  <line x1="16" y1="17" x2="8" y2="17"></line>
                </svg>
              </div>
              <p className="empty-state-text">No reservations scheduled for today</p>
            </div>
          )}
        </div>
      </div>

      {/* Quick Actions */}
      <div className="dashboard-content-wrapper">
        <div className="glass-card" style={{ marginBottom: '2rem' }}>
          <h2 className="section-title section-title-center">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
            </svg>
            <span>Quick Actions</span>
          </h2>
          <div className="actions-grid">
            <a href="/reservations" className="action-button action-card-1">
              <div className="action-icon-wrapper">
                <div className="action-icon action-icon-1">
                  <svg width="36" height="36" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                    <polyline points="14 2 14 8 20 8"></polyline>
                    <line x1="16" y1="13" x2="8" y2="13"></line>
                    <line x1="16" y1="17" x2="8" y2="17"></line>
                  </svg>
                </div>
              </div>
              <div className="action-content">
                <h3 className="action-title">Manage Reservations</h3>
                <p className="action-description">View and manage all guest reservations</p>
              </div>
            </a>

            <a href="/rooms" className="action-button action-card-2">
              <div className="action-icon-wrapper">
                <div className="action-icon action-icon-2">
                  <svg width="36" height="36" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
                    <polyline points="9 22 9 12 15 12 15 22"></polyline>
                  </svg>
                </div>
              </div>
              <div className="action-content">
                <h3 className="action-title">View Rooms</h3>
                <p className="action-description">Check room availability and status</p>
              </div>
            </a>

            <a href="/reservations?status=confirmed" className="action-button action-card-3">
              <div className="action-icon-wrapper">
                <div className="action-icon action-icon-3">
                  <svg width="36" height="36" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                    <polyline points="22 4 12 14.01 9 11.01"></polyline>
                  </svg>
                </div>
              </div>
              <div className="action-content">
                <h3 className="action-title">Pending Check-ins</h3>
                <p className="action-description">Process guest check-ins</p>
              </div>
            </a>

            <a href="/reservations?status=checked-in" className="action-button action-card-4">
              <div className="action-icon-wrapper">
                <div className="action-icon action-icon-4">
                  <svg width="36" height="36" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                    <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
                    <circle cx="9" cy="7" r="4"></circle>
                    <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
                    <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
                  </svg>
                </div>
              </div>
              <div className="action-content">
                <h3 className="action-title">Active Guests</h3>
                <p className="action-description">View currently checked-in guests</p>
              </div>
            </a>
          </div>
        </div>
      </div>

      <GuestFooter />
      <Chat isOpen={chatOpen} onClose={() => setChatOpen(false)} />
    </div>
  );
};

export default ReceptionistDashboard;
