import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import GuestFooter from '../../components/GuestFooter.jsx';
import '../guest/GuestDashboard.css';

const ReceptionistReservations = () => {
  const { user } = useContext(AuthContext);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState({ status: '' });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchReservations();
  }, [filter]);

  const fetchReservations = async () => {
    try {
      const params = {};
      if (filter.status) params.status = filter.status;
      
      const response = await axios.get('/api/reservations', { params });
      setReservations(response.data.reservations || []);
    } catch (error) {
      console.error('Error fetching reservations:', error);
      alert('Error loading reservations');
    } finally {
      setLoading(false);
    }
  };

  const handleCheckIn = async (id) => {
    if (!window.confirm('Are you sure you want to check-in this guest?')) {
      return;
    }
    try {
      await axios.put(`/api/reservations/${id}/checkin`);
      alert('Guest checked in successfully!');
      fetchReservations();
    } catch (error) {
      alert(error.response?.data?.message || 'Error checking in guest');
    }
  };

  const handleCheckOut = async (id) => {
    if (!window.confirm('Are you sure you want to check-out this guest?')) {
      return;
    }
    try {
      await axios.put(`/api/reservations/${id}/checkout`);
      alert('Guest checked out successfully!');
      fetchReservations();
    } catch (error) {
      alert(error.response?.data?.message || 'Error checking out guest');
    }
  };

  const handleCancel = async (id) => {
    if (!window.confirm('Are you sure you want to cancel this reservation?')) {
      return;
    }
    try {
      await axios.put(`/api/reservations/${id}`, { status: 'cancelled' });
      alert('Reservation cancelled successfully!');
      fetchReservations();
    } catch (error) {
      alert(error.response?.data?.message || 'Error cancelling reservation');
    }
  };

  const filteredReservations = reservations.filter(reservation => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return (
      reservation.confirmationNumber?.toLowerCase().includes(searchLower) ||
      reservation.guestId?.firstName?.toLowerCase().includes(searchLower) ||
      reservation.guestId?.lastName?.toLowerCase().includes(searchLower) ||
      reservation.guestId?.email?.toLowerCase().includes(searchLower) ||
      reservation.roomId?.roomNumber?.toLowerCase().includes(searchLower)
    );
  });

  const confirmedCount = reservations.filter(r => r.status === 'confirmed').length;
  const checkedInCount = reservations.filter(r => r.status === 'checked-in').length;
  const totalAmount = reservations.reduce((sum, r) => sum + (r.totalAmount || 0), 0);

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div className="guest-page-container">
      <div className="page-header">
        <div className="page-header-content">
          <div className="page-title-section">
            <div className="page-icon">
              <svg width="32" height="32" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                <polyline points="14 2 14 8 20 8"></polyline>
                <line x1="16" y1="13" x2="8" y2="13"></line>
                <line x1="16" y1="17" x2="8" y2="17"></line>
                <polyline points="10 9 9 9 8 9"></polyline>
              </svg>
            </div>
            <div>
              <h1 className="page-title">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
                </svg>
                <span>Reservations Management</span>
              </h1>
              <p className="page-subtitle">Manage guest check-ins and check-outs</p>
            </div>
          </div>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="page-stats-grid">
        <div className="page-stat-card">
          <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #d4af37 0%, #b8941f 100%)' }}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
              <polyline points="22 4 12 14.01 9 11.01"></polyline>
            </svg>
          </div>
          <div className="stat-card-content">
            <div className="stat-card-value">{confirmedCount}</div>
            <div className="stat-card-label">Confirmed</div>
          </div>
        </div>
        <div className="page-stat-card">
          <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #4CAF50 0%, #2E7D32 100%)' }}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
              <polyline points="9 22 9 12 15 12 15 22"></polyline>
            </svg>
          </div>
          <div className="stat-card-content">
            <div className="stat-card-value">{checkedInCount}</div>
            <div className="stat-card-label">Checked In</div>
          </div>
        </div>
        <div className="page-stat-card">
          <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #8b6f47 0%, #6b5a4a 100%)' }}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <line x1="12" y1="1" x2="12" y2="23"></line>
              <path d="M17 5H9.5a3.5 3.5 0 0 0 0 7h5a3.5 3.5 0 0 1 0 7H6"></path>
            </svg>
          </div>
          <div className="stat-card-content">
            <div className="stat-card-value">${totalAmount.toFixed(2)}</div>
            <div className="stat-card-label">Total Value</div>
          </div>
        </div>
        <div className="page-stat-card">
          <div className="stat-card-icon" style={{ background: 'linear-gradient(135deg, #4ECDC4 0%, #45B7B8 100%)' }}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
              <polyline points="14 2 14 8 20 8"></polyline>
            </svg>
          </div>
          <div className="stat-card-content">
            <div className="stat-card-value">{reservations.length}</div>
            <div className="stat-card-label">Total Reservations</div>
          </div>
        </div>
      </div>

      {/* Filters and Search */}
      <div className="glass-card" style={{ marginBottom: '2rem' }}>
        <h2 className="section-title">
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
          </svg>
          <span>Filters</span>
        </h2>
        <div className="form-row" style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginTop: '1rem' }}>
          <div className="form-group">
            <label>Search</label>
            <input
              type="text"
              className="form-control"
              placeholder="Search by confirmation #, guest name, email, or room number..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Filter by Status</label>
            <select
              className="filter-select"
              value={filter.status}
              onChange={(e) => setFilter({ ...filter, status: e.target.value })}
            >
              <option value="">All Status</option>
              <option value="pending">Pending</option>
              <option value="confirmed">Confirmed</option>
              <option value="checked-in">Checked-in</option>
              <option value="checked-out">Checked-out</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>
        </div>
      </div>

      {/* Reservations Table */}
      <div className="glass-card">
        <h2 className="section-title">
          <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
          </svg>
          <span>Reservation History</span>
        </h2>
        
        {filteredReservations.length > 0 ? (
          <div style={{ overflowX: 'auto', marginTop: '1.5rem' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Confirmation #</th>
                  <th>Guest Name</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Room</th>
                  <th>Check-in</th>
                  <th>Check-out</th>
                  <th>Guests</th>
                  <th>Amount</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredReservations.map(reservation => (
                  <tr key={reservation._id}>
                    <td>
                      <strong>{reservation.confirmationNumber}</strong>
                    </td>
                    <td>
                      {reservation.guestId?.firstName} {reservation.guestId?.lastName}
                    </td>
                    <td>{reservation.guestId?.email || '-'}</td>
                    <td>{reservation.guestId?.phone || '-'}</td>
                    <td>
                      <strong>{reservation.roomId?.roomNumber}</strong>
                      <br />
                      <small>{reservation.roomId?.roomType}</small>
                    </td>
                    <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                    <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                    <td>{reservation.numberOfGuests}</td>
                    <td>${reservation.totalAmount?.toFixed(2)}</td>
                    <td>
                      <span className={`badge ${
                        reservation.status === 'confirmed' ? 'badge-success' :
                        reservation.status === 'checked-in' ? 'badge-info' :
                        reservation.status === 'checked-out' ? 'badge-secondary' :
                        reservation.status === 'cancelled' ? 'badge-danger' :
                        'badge-warning'
                      }`}>
                        {reservation.status}
                      </span>
                    </td>
                    <td>
                      <div style={{ display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
                        {reservation.status === 'confirmed' && (
                          <button
                            className="btn btn-success"
                            onClick={() => handleCheckIn(reservation._id)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                            title="Check-in Guest"
                          >
                            Check-in
                          </button>
                        )}
                        {reservation.status === 'checked-in' && (
                          <button
                            className="btn btn-primary"
                            onClick={() => handleCheckOut(reservation._id)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                            title="Check-out Guest"
                          >
                            Check-out
                          </button>
                        )}
                        {['pending', 'confirmed'].includes(reservation.status) && (
                          <button
                            className="btn btn-danger"
                            onClick={() => handleCancel(reservation._id)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                            title="Cancel Reservation"
                          >
                            Cancel
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="empty-state">
            <div className="empty-state-icon">
              <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                <polyline points="14 2 14 8 20 8"></polyline>
                <line x1="16" y1="13" x2="8" y2="13"></line>
                <line x1="16" y1="17" x2="8" y2="17"></line>
              </svg>
            </div>
            <p className="empty-state-text">No reservations found</p>
          </div>
        )}
      </div>
      <GuestFooter />
    </div>
  );
};

export default ReceptionistReservations;
