import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const HousekeepingDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myTasks, setMyTasks] = useState([]);
  const [pendingTasks, setPendingTasks] = useState(0);
  const [inProgressTasks, setInProgressTasks] = useState(0);
  const [completedToday, setCompletedToday] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHousekeepingStats();
  }, []);

  const fetchHousekeepingStats = async () => {
    try {
      const response = await axios.get('/api/housekeeping', { 
        params: { assignedTo: user?._id } 
      });
      
      const tasks = response.data.tasks || [];
      setMyTasks(tasks);
      setPendingTasks(tasks.filter(t => t.status === 'pending').length);
      setInProgressTasks(tasks.filter(t => t.status === 'in-progress').length);
      setCompletedToday(tasks.filter(t => t.status === 'completed' && new Date(t.completedDate).toDateString() === new Date().toDateString()).length);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div style={{ padding: '20px', background: '#f6f3ee', minHeight: '100vh' }}>
      {/* Header */}
      <div style={{ 
        background: '#ffffff', 
        padding: '20px 24px', 
        borderRadius: '16px', 
        marginBottom: '20px',
        boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
        border: '1px solid #e7dfd4',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          <h1 style={{ color: '#4e342e', marginBottom: '8px', fontSize: '1.8rem', fontWeight: 700 }}>Housekeeping Dashboard</h1>
          <p style={{ color: '#8d6e63', fontSize: '14px', margin: 0 }}>Welcome, {user?.firstName}! Manage your assigned tasks</p>
        </div>
        <button 
          onClick={handleLogout} 
          className="btn btn-danger" 
          style={{ 
            padding: '10px 24px',
            borderRadius: '8px',
            fontWeight: 600,
            fontSize: '14px'
          }}
        >
          LOGOUT
        </button>
      </div>

      {/* Stats Cards */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '18px', marginBottom: '20px' }}>
        <div className="card">
          <h3 style={{ color: '#4e342e', fontSize: '14px', marginBottom: '8px', fontWeight: 600 }}>My Tasks</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#6d4c41', margin: '8px 0' }}>{myTasks.length}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Total assigned tasks</span>
        </div>
        <div className="card">
          <h3 style={{ color: '#4e342e', fontSize: '14px', marginBottom: '8px', fontWeight: 600 }}>Pending Tasks</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#ffb74d', margin: '8px 0' }}>{pendingTasks}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Awaiting start</span>
        </div>
        <div className="card">
          <h3 style={{ color: '#4e342e', fontSize: '14px', marginBottom: '8px', fontWeight: 600 }}>In Progress</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#c7925b', margin: '8px 0' }}>{inProgressTasks}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Currently working</span>
        </div>
        <div className="card">
          <h3 style={{ color: '#4e342e', fontSize: '14px', marginBottom: '8px', fontWeight: 600 }}>Completed Today</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#ffcc80', margin: '8px 0' }}>{completedToday}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Finished today</span>
        </div>
      </div>

      {/* Recent Tasks */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <h2 style={{ color: '#4e342e', marginBottom: '20px', fontSize: '1.5rem', fontWeight: 600 }}>My Recent Tasks</h2>
        {myTasks.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Room</th>
                  <th>Task Type</th>
                  <th>Scheduled Date</th>
                  <th>Priority</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {myTasks.slice(0, 5).map(task => (
                  <tr key={task._id}>
                    <td style={{ fontWeight: 600, color: '#4e342e' }}>
                      {task.roomId?.roomNumber} - {task.roomId?.roomType}
                    </td>
                    <td>
                      <span style={{
                        padding: '4px 12px',
                        borderRadius: '12px',
                        fontSize: '12px',
                        fontWeight: 600,
                        textTransform: 'capitalize',
                        background: 'linear-gradient(135deg, #ffe0b2 0%, #ffcc80 100%)',
                        color: '#4e342e'
                      }}>
                        {task.taskType}
                      </span>
                    </td>
                    <td>{new Date(task.scheduledDate).toLocaleString()}</td>
                    <td>
                      <span className={`badge ${
                        task.priority === 'urgent' ? 'badge-danger' :
                        task.priority === 'high' ? 'badge-warning' :
                        'badge-info'
                      }`}>
                        {task.priority}
                      </span>
                    </td>
                    <td>
                      <span className={`badge ${
                        task.status === 'completed' ? 'badge-success' :
                        task.status === 'in-progress' ? 'badge-info' :
                        'badge-warning'
                      }`}>
                        {task.status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div style={{ textAlign: 'center', padding: '40px', color: '#8d6e63' }}>
            <p style={{ fontSize: '18px', marginBottom: '10px' }}>No tasks assigned</p>
            <p style={{ fontSize: '14px' }}>You'll see your assigned tasks here</p>
          </div>
        )}
