import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const HousekeepingDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [myTasks, setMyTasks] = useState([]);
  const [pendingTasks, setPendingTasks] = useState(0);
  const [inProgressTasks, setInProgressTasks] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchHousekeepingStats();
  }, []);

  const fetchHousekeepingStats = async () => {
    try {
      const response = await axios.get('/api/housekeeping', { 
        params: { assignedTo: user?._id } 
      });
      
      const tasks = response.data.tasks || [];
      setMyTasks(tasks);
      setPendingTasks(tasks.filter(t => t.status === 'pending').length);
      setInProgressTasks(tasks.filter(t => t.status === 'in-progress').length);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
          <h1>Housekeeping Dashboard</h1>
          <p style={{ color: '#666', marginTop: '5px' }}>Welcome, {user?.firstName}! Manage your assigned tasks</p>
        </div>
        <button onClick={handleLogout} className="btn btn-danger" style={{ marginLeft: 'auto' }}>
          Logout
        </button>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
        <div className="card">
          <h3>My Tasks</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#007bff' }}>{myTasks.length}</p>
        </div>
        <div className="card">
          <h3>Pending Tasks</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#ffc107' }}>{pendingTasks}</p>
        </div>
        <div className="card">
          <h3>In Progress</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#17a2b8' }}>{inProgressTasks}</p>
        </div>
        <div className="card">
          <h3>Completed Today</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#28a745' }}>
            {myTasks.filter(t => t.status === 'completed' && new Date(t.completedDate).toDateString() === new Date().toDateString()).length}
          </p>
        </div>
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h2>My Recent Tasks</h2>
        {myTasks.length > 0 ? (
          <table className="table" style={{ marginTop: '15px' }}>
            <thead>
              <tr>
                <th>Room</th>
                <th>Task Type</th>
                <th>Scheduled Date</th>
                <th>Priority</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {myTasks.slice(0, 5).map(task => (
                <tr key={task._id}>
                  <td>{task.roomId?.roomNumber} - {task.roomId?.roomType}</td>
                  <td>{task.taskType}</td>
                  <td>{new Date(task.scheduledDate).toLocaleString()}</td>
                  <td>
                    <span className={`badge ${
                      task.priority === 'urgent' ? 'badge-danger' :
                      task.priority === 'high' ? 'badge-warning' :
                      'badge-info'
                    }`}>
                      {task.priority}
                    </span>
                  </td>
                  <td>
                    <span className={`badge ${
                      task.status === 'completed' ? 'badge-success' :
                      task.status === 'in-progress' ? 'badge-info' :
                      'badge-warning'
                    }`}>
                      {task.status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ marginTop: '15px', color: '#666' }}>No tasks assigned</p>
        )}
      </div>

      <div className="card" style={{ marginTop: '20px' }}>
        <h2>Quick Actions</h2>
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap', marginTop: '20px' }}>
          <a href="/housekeeping" className="btn btn-primary">View All Tasks</a>
          <a href="/maintenance" className="btn btn-primary">Maintenance Requests</a>
        </div>
      </div>
    </div>
  );
};

export default HousekeepingDashboard;
