import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';
import AdminLayout from '../../components/AdminLayout.jsx';

const ReceptionistDashboard = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalRooms: 0,
    availableRooms: 0,
    occupiedRooms: 0,
    cleaningRooms: 0,
    pendingReservations: 0,
    checkedInGuests: 0
  });
  const [loading, setLoading] = useState(true);
  const [todayReservations, setTodayReservations] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      const reservationsResponse = await axios.get('/api/reservations');
      const allReservations = reservationsResponse.data.reservations || [];

      const todayCheckIns = allReservations.filter(r => {
        const checkInDate = new Date(r.checkInDate);
        checkInDate.setHours(0, 0, 0, 0);
        return checkInDate.getTime() === today.getTime() && r.status === 'confirmed';
      });

      const todayCheckOuts = allReservations.filter(r => {
        const checkOutDate = new Date(r.checkOutDate);
        checkOutDate.setHours(0, 0, 0, 0);
        return checkOutDate.getTime() === today.getTime() && r.status === 'checked-in';
      });

      const checkedInGuests = allReservations.filter(r => r.status === 'checked-in').length;
      const pendingReservations = allReservations.filter(r => r.status === 'pending' || r.status === 'confirmed').length;

      const roomsResponse = await axios.get('/api/rooms');
      const allRooms = roomsResponse.data.rooms || [];

      const availableRooms = allRooms.filter(r => r.status === 'available').length;
      const occupiedRooms = allRooms.filter(r => r.status === 'occupied').length;
      const cleaningRooms = allRooms.filter(r => r.status === 'cleaning').length;

      const todayRes = allReservations.filter(r => {
        const checkIn = new Date(r.checkInDate);
        checkIn.setHours(0, 0, 0, 0);
        const checkOut = new Date(r.checkOutDate);
        checkOut.setHours(0, 0, 0, 0);
        return (checkIn.getTime() === today.getTime() || checkOut.getTime() === today.getTime()) &&
               ['confirmed', 'checked-in'].includes(r.status);
      }).slice(0, 5);

      setStats({
        todayCheckIns: todayCheckIns.length,
        todayCheckOuts: todayCheckOuts.length,
        totalRooms: allRooms.length,
        availableRooms,
        occupiedRooms,
        cleaningRooms,
        pendingReservations,
        checkedInGuests
      });

      setTodayReservations(todayRes);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  const occupiedRooms = stats.occupiedRooms || 0;
  const totalRooms = stats.totalRooms || 0;
  const availableRooms = stats.availableRooms || 0;
  const occupancyPercent = totalRooms > 0 ? Math.round((occupiedRooms / totalRooms) * 100) : 0;

  const dashboardTitle = 'Dashboard';
  const dashboardSubtitle = 'Coffee-brown overview of your LuxuryStay reception operations';

  return (
    <AdminLayout title={dashboardTitle} subtitle={dashboardSubtitle}>
      {/* Top metric cards */}
      <div className="grid grid-4 mb-3">
        <div className="card">
          <h3>Today's Check-ins</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#6d4c41' }}>{stats.todayCheckIns || 0}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Guests arriving today</span>
        </div>
        <div className="card">
          <h3>Today's Check-outs</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#a1887f' }}>{stats.todayCheckOuts || 0}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Departing guests</span>
        </div>
        <div className="card">
          <h3>Occupancy Rate</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#ffb74d' }}>{occupancyPercent || 0}%</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>{stats.occupiedRooms || 0} / {stats.totalRooms || 0} rooms occupied</span>
        </div>
        <div className="card">
          <h3>Checked-in Guests</h3>
          <p style={{ fontSize: '32px', fontWeight: 'bold', color: '#ffcc80' }}>{stats.checkedInGuests || 0}</p>
          <span style={{ fontSize: '12px', color: '#8d6e63' }}>Currently in hotel</span>
        </div>
      </div>

      {/* Chart + side widgets layout similar to Admin Dashboard */}
      <div className="admin-dashboard-grid">
        {/* Today's Reservations */}
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '12px' }}>
            <div>
              <h2>Today's Reservations</h2>
              <p style={{ fontSize: '12px', color: '#8d6e63' }}>Check-ins and check-outs scheduled</p>
            </div>
            <button 
              className="btn btn-primary" 
              onClick={() => navigate('/reservations')}
              style={{ padding: '6px 12px', fontSize: '12px' }}
            >
              View All
            </button>
          </div>

          {todayReservations.length > 0 ? (
            <div style={{ overflowX: 'auto' }}>
              <table className="table" style={{ fontSize: '13px' }}>
                <thead>
                  <tr>
                    <th>Confirmation #</th>
                    <th>Guest Name</th>
                    <th>Room</th>
                    <th>Check-in</th>
                    <th>Check-out</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {todayReservations.map(reservation => (
                    <tr key={reservation._id}>
                      <td><strong>{reservation.confirmationNumber}</strong></td>
                      <td>{reservation.guestId?.firstName} {reservation.guestId?.lastName}</td>
                      <td>{reservation.roomId?.roomNumber} - {reservation.roomId?.roomType}</td>
                      <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                      <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                      <td>
                        <span className={`badge ${
                          reservation.status === 'confirmed' ? 'badge-success' :
                          reservation.status === 'checked-in' ? 'badge-info' :
                          'badge-warning'
                        }`}>
                          {reservation.status}
                        </span>
                      </td>
                      <td>
                        {reservation.status === 'confirmed' && (
                          <button
                            className="btn btn-success"
                            onClick={() => navigate(`/reservations`)}
                            style={{ padding: '4px 8px', fontSize: '11px' }}
                          >
                            Check-in
                          </button>
                        )}
                        {reservation.status === 'checked-in' && (
                          <button
                            className="btn btn-primary"
                            onClick={() => navigate(`/reservations`)}
                            style={{ padding: '4px 8px', fontSize: '11px' }}
                          >
                            Check-out
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div style={{ textAlign: 'center', padding: '40px', color: '#8d6e63' }}>
              <p>No reservations scheduled for today</p>
            </div>
          )}
        </div>

        {/* Side widgets */}
        <div style={{ display: 'flex', flexDirection: 'column', gap: 16 }}>
          <div className="card">
            <h3>Room Status</h3>
            <ul style={{ listStyle: 'none', padding: 0, marginTop: 10, fontSize: 13 }}>
              <li style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
                <span>Available rooms</span>
                <span className="badge badge-success">{stats.availableRooms || 0}</span>
              </li>
              <li style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
                <span>Occupied rooms</span>
                <span className="badge badge-info">{stats.occupiedRooms || 0}</span>
              </li>
              <li style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
                <span>Cleaning</span>
                <span className="badge badge-warning">{stats.cleaningRooms || 0}</span>
              </li>
            </ul>
          </div>

          <div className="card">
            <h3>Quick Links</h3>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 8, marginTop: 10 }}>
              <a href="/reservations" className="btn btn-primary">Manage Reservations</a>
              <a href="/rooms" className="btn btn-primary">View Rooms</a>
              <a href="/reservations?status=confirmed" className="btn btn-primary">Pending Check-ins</a>
            </div>
          </div>
        </div>
      </div>

      {/* Additional analytics widgets */}
      <div className="grid grid-2 mt-3">
        <div className="card">
          <h3>Room Status Snapshot</h3>
          <p style={{ fontSize: '12px', color: '#8d6e63', marginBottom: '12px' }}>Occupancy vs availability</p>
          <div style={{ display: 'flex', alignItems: 'center', gap: 24 }}>
            <div
              style={{
                position: 'relative',
                width: 120,
                height: 120,
                borderRadius: '50%',
                background: `conic-gradient(#ffcc80 0deg, #ffcc80 ${Math.min(occupancyPercent, 100) * 3.6}deg, #f3e5dc ${Math.min(occupancyPercent, 100) * 3.6}deg)`,
                boxShadow: '0 6px 16px rgba(0,0,0,0.15)',
              }}
            >
              <div
                style={{
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)',
                  width: 76,
                  height: 76,
                  borderRadius: '50%',
                  background: '#ffffff',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  flexDirection: 'column',
                }}
              >
                <span style={{ fontSize: 20, fontWeight: 700, color: '#6d4c41' }}>{occupancyPercent || 0}%</span>
                <span style={{ fontSize: 10, color: '#8d6e63' }}>Occupied</span>
              </div>
            </div>
            <div style={{ fontSize: 13, color: '#5d4037' }}>
              <div style={{ marginBottom: 6 }}>
                <strong>{occupiedRooms}</strong> occupied rooms
              </div>
              <div style={{ marginBottom: 6 }}>
                <strong>{availableRooms}</strong> available rooms
              </div>
              <div>
                <strong>{totalRooms}</strong> total rooms
              </div>
            </div>
          </div>
        </div>

        <div className="card">
          <h3>Today's Summary</h3>
          <p style={{ fontSize: '12px', color: '#8d6e63', marginBottom: '12px' }}>Key metrics for today</p>
          <div style={{ display: 'flex', flexDirection: 'column', gap: 12 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '8px 0', borderBottom: '1px solid #f3e5dc' }}>
              <span style={{ fontSize: '13px', color: '#5d4037' }}>Pending Reservations</span>
              <span style={{ fontSize: '16px', fontWeight: 700, color: '#6d4c41' }}>{stats.pendingReservations || 0}</span>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '8px 0', borderBottom: '1px solid #f3e5dc' }}>
              <span style={{ fontSize: '13px', color: '#5d4037' }}>Rooms Cleaning</span>
              <span style={{ fontSize: '16px', fontWeight: 700, color: '#ffb74d' }}>{stats.cleaningRooms || 0}</span>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '8px 0' }}>
              <span style={{ fontSize: '13px', color: '#5d4037' }}>Total Rooms</span>
              <span style={{ fontSize: '16px', fontWeight: 700, color: '#8d6e63' }}>{stats.totalRooms || 0}</span>
            </div>
          </div>
        </div>
      </div>
    </AdminLayout>
  );
};

export default ReceptionistDashboard;
