import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const ReceptionistReservations = () => {
  const { user } = useContext(AuthContext);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState({ status: '' });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchReservations();
  }, [filter]);

  const fetchReservations = async () => {
    try {
      const params = {};
      if (filter.status) params.status = filter.status;
      
      const response = await axios.get('/api/reservations', { params });
      setReservations(response.data.reservations || []);
    } catch (error) {
      console.error('Error fetching reservations:', error);
      alert('Error loading reservations');
    } finally {
      setLoading(false);
    }
  };

  const handleCheckIn = async (id) => {
    if (!window.confirm('Are you sure you want to check-in this guest?')) {
      return;
    }
    try {
      await axios.put(`/api/reservations/${id}/checkin`);
      alert('Guest checked in successfully!');
      fetchReservations();
    } catch (error) {
      alert(error.response?.data?.message || 'Error checking in guest');
    }
  };

  const handleCheckOut = async (id) => {
    if (!window.confirm('Are you sure you want to check-out this guest?')) {
      return;
    }
    try {
      await axios.put(`/api/reservations/${id}/checkout`);
      alert('Guest checked out successfully!');
      fetchReservations();
    } catch (error) {
      alert(error.response?.data?.message || 'Error checking out guest');
    }
  };

  const handleCancel = async (id) => {
    if (!window.confirm('Are you sure you want to cancel this reservation?')) {
      return;
    }
    try {
      await axios.put(`/api/reservations/${id}`, { status: 'cancelled' });
      alert('Reservation cancelled successfully!');
      fetchReservations();
    } catch (error) {
      alert(error.response?.data?.message || 'Error cancelling reservation');
    }
  };

  // Filter reservations by search term
  const filteredReservations = reservations.filter(reservation => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return (
      reservation.confirmationNumber?.toLowerCase().includes(searchLower) ||
      reservation.guestId?.firstName?.toLowerCase().includes(searchLower) ||
      reservation.guestId?.lastName?.toLowerCase().includes(searchLower) ||
      reservation.guestId?.email?.toLowerCase().includes(searchLower) ||
      reservation.roomId?.roomNumber?.toLowerCase().includes(searchLower)
    );
  });

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
          <h1>Reservations Management</h1>
          <p style={{ color: '#666', marginTop: '5px' }}>Manage guest check-ins and check-outs</p>
        </div>
      </div>

      {/* Filters and Search */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px', alignItems: 'end' }}>
          <div className="form-group">
            <label>Search</label>
            <input
              type="text"
              placeholder="Search by confirmation #, guest name, email, or room number..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
          <div className="form-group">
            <label>Filter by Status</label>
            <select
              value={filter.status}
              onChange={(e) => setFilter({ ...filter, status: e.target.value })}
              style={{ width: '100%', padding: '8px' }}
            >
              <option value="">All Status</option>
              <option value="pending">Pending</option>
              <option value="confirmed">Confirmed</option>
              <option value="checked-in">Checked-in</option>
              <option value="checked-out">Checked-out</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>
        </div>
      </div>

      {/* Reservations Table */}
      <div className="card">
        <h2 style={{ marginBottom: '15px' }}>Reservations ({filteredReservations.length})</h2>
        
        {filteredReservations.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Confirmation #</th>
                  <th>Guest Name</th>
                  <th>Email</th>
                  <th>Phone</th>
                  <th>Room</th>
                  <th>Check-in</th>
                  <th>Check-out</th>
                  <th>Guests</th>
                  <th>Amount</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredReservations.map(reservation => (
                  <tr key={reservation._id}>
                    <td>
                      <strong>{reservation.confirmationNumber}</strong>
                    </td>
                    <td>
                      {reservation.guestId?.firstName} {reservation.guestId?.lastName}
                    </td>
                    <td>{reservation.guestId?.email || '-'}</td>
                    <td>{reservation.guestId?.phone || '-'}</td>
                    <td>
                      <strong>{reservation.roomId?.roomNumber}</strong>
                      <br />
                      <small>{reservation.roomId?.roomType}</small>
                    </td>
                    <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                    <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                    <td>{reservation.numberOfGuests}</td>
                    <td>${reservation.totalAmount?.toFixed(2)}</td>
                    <td>
                      <span className={`badge ${
                        reservation.status === 'confirmed' ? 'badge-success' :
                        reservation.status === 'checked-in' ? 'badge-info' :
                        reservation.status === 'checked-out' ? 'badge-secondary' :
                        reservation.status === 'cancelled' ? 'badge-danger' :
                        'badge-warning'
                      }`}>
                        {reservation.status}
                      </span>
                    </td>
                    <td>
                      <div style={{ display: 'flex', gap: '5px', flexWrap: 'wrap' }}>
                        {reservation.status === 'confirmed' && (
                          <button
                            className="btn btn-success"
                            onClick={() => handleCheckIn(reservation._id)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                            title="Check-in Guest"
                          >
                            Check-in
                          </button>
                        )}
                        {reservation.status === 'checked-in' && (
                          <button
                            className="btn btn-primary"
                            onClick={() => handleCheckOut(reservation._id)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                            title="Check-out Guest"
                          >
                            Check-out
                          </button>
                        )}
                        {['pending', 'confirmed'].includes(reservation.status) && (
                          <button
                            className="btn btn-danger"
                            onClick={() => handleCancel(reservation._id)}
                            style={{ padding: '5px 10px', fontSize: '12px' }}
                            title="Cancel Reservation"
                          >
                            Cancel
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
            <p>No reservations found</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReceptionistReservations;
