import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const ReceptionistRooms = () => {
  const { user } = useContext(AuthContext);
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState({ status: '', roomType: '' });
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchRooms();
  }, [filter]);

  const fetchRooms = async () => {
    try {
      const params = {};
      if (filter.status) params.status = filter.status;
      if (filter.roomType) params.roomType = filter.roomType;
      
      const response = await axios.get('/api/rooms', { params });
      setRooms(response.data.rooms || []);
    } catch (error) {
      console.error('Error fetching rooms:', error);
      alert('Error loading rooms');
    } finally {
      setLoading(false);
    }
  };

  const handleStatusUpdate = async (id, newStatus) => {
    if (!window.confirm(`Are you sure you want to change room status to "${newStatus}"?`)) {
      return;
    }
    try {
      await axios.put(`/api/rooms/${id}`, { status: newStatus });
      alert('Room status updated successfully!');
      fetchRooms();
    } catch (error) {
      alert(error.response?.data?.message || 'Error updating room status');
    }
  };

  // Filter rooms by search term
  const filteredRooms = rooms.filter(room => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return (
      room.roomNumber?.toLowerCase().includes(searchLower) ||
      room.roomType?.toLowerCase().includes(searchLower) ||
      room.floor?.toString().includes(searchLower)
    );
  });

  // Count rooms by status
  const statusCounts = {
    available: rooms.filter(r => r.status === 'available').length,
    occupied: rooms.filter(r => r.status === 'occupied').length,
    cleaning: rooms.filter(r => r.status === 'cleaning').length,
    maintenance: rooms.filter(r => r.status === 'maintenance').length,
    reserved: rooms.filter(r => r.status === 'reserved').length
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
          <h1>Room Management</h1>
          <p style={{ color: '#666', marginTop: '5px' }}>View and update room status</p>
        </div>
      </div>

      {/* Status Summary */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '15px', marginBottom: '20px' }}>
        <div className="card" style={{ textAlign: 'center', background: '#d4edda', border: '2px solid #28a745' }}>
          <h3 style={{ color: '#28a745', margin: '0', fontSize: '24px' }}>{statusCounts.available}</h3>
          <p style={{ margin: '5px 0 0 0', color: '#155724' }}>Available</p>
        </div>
        <div className="card" style={{ textAlign: 'center', background: '#cce5ff', border: '2px solid #007bff' }}>
          <h3 style={{ color: '#007bff', margin: '0', fontSize: '24px' }}>{statusCounts.occupied}</h3>
          <p style={{ margin: '5px 0 0 0', color: '#004085' }}>Occupied</p>
        </div>
        <div className="card" style={{ textAlign: 'center', background: '#fff3cd', border: '2px solid #ffc107' }}>
          <h3 style={{ color: '#856404', margin: '0', fontSize: '24px' }}>{statusCounts.cleaning}</h3>
          <p style={{ margin: '5px 0 0 0', color: '#856404' }}>Cleaning</p>
        </div>
        <div className="card" style={{ textAlign: 'center', background: '#f8d7da', border: '2px solid #dc3545' }}>
          <h3 style={{ color: '#dc3545', margin: '0', fontSize: '24px' }}>{statusCounts.maintenance}</h3>
          <p style={{ margin: '5px 0 0 0', color: '#721c24' }}>Maintenance</p>
        </div>
        <div className="card" style={{ textAlign: 'center', background: '#e2e3e5', border: '2px solid #6c757d' }}>
          <h3 style={{ color: '#6c757d', margin: '0', fontSize: '24px' }}>{statusCounts.reserved}</h3>
          <p style={{ margin: '5px 0 0 0', color: '#383d41' }}>Reserved</p>
        </div>
      </div>

      {/* Filters and Search */}
      <div className="card" style={{ marginBottom: '20px' }}>
        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: '15px', alignItems: 'end' }}>
          <div className="form-group">
            <label>Search</label>
            <input
              type="text"
              placeholder="Search by room number, type, or floor..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ width: '100%' }}
            />
          </div>
          <div className="form-group">
            <label>Filter by Status</label>
            <select
              value={filter.status}
              onChange={(e) => setFilter({ ...filter, status: e.target.value })}
              style={{ width: '100%', padding: '8px' }}
            >
              <option value="">All Status</option>
              <option value="available">Available</option>
              <option value="occupied">Occupied</option>
              <option value="cleaning">Cleaning</option>
              <option value="maintenance">Maintenance</option>
              <option value="reserved">Reserved</option>
            </select>
          </div>
          <div className="form-group">
            <label>Filter by Type</label>
            <select
              value={filter.roomType}
              onChange={(e) => setFilter({ ...filter, roomType: e.target.value })}
              style={{ width: '100%', padding: '8px' }}
            >
              <option value="">All Types</option>
              <option value="Single">Single</option>
              <option value="Double">Double</option>
              <option value="Suite">Suite</option>
              <option value="Deluxe">Deluxe</option>
              <option value="Presidential">Presidential</option>
            </select>
          </div>
        </div>
      </div>

      {/* Rooms Table */}
      <div className="card">
        <h2 style={{ marginBottom: '15px' }}>Rooms ({filteredRooms.length})</h2>
        
        {filteredRooms.length > 0 ? (
          <div style={{ overflowX: 'auto' }}>
            <table className="table">
              <thead>
                <tr>
                  <th>Room Number</th>
                  <th>Type</th>
                  <th>Floor</th>
                  <th>Price/Night</th>
                  <th>Max Occupancy</th>
                  <th>Status</th>
                  <th>Update Status</th>
                </tr>
              </thead>
              <tbody>
                {filteredRooms.map(room => (
                  <tr key={room._id}>
                    <td>
                      <strong>{room.roomNumber}</strong>
                    </td>
                    <td>{room.roomType}</td>
                    <td>Floor {room.floor}</td>
                    <td>${room.pricePerNight?.toFixed(2)}</td>
                    <td>{room.maxOccupancy} guests</td>
                    <td>
                      <span className={`badge ${
                        room.status === 'available' ? 'badge-success' :
                        room.status === 'occupied' ? 'badge-info' :
                        room.status === 'cleaning' ? 'badge-warning' :
                        room.status === 'maintenance' ? 'badge-danger' :
                        'badge-secondary'
                      }`}>
                        {room.status}
                      </span>
                    </td>
                    <td>
                      <select
                        value={room.status}
                        onChange={(e) => handleStatusUpdate(room._id, e.target.value)}
                        style={{ padding: '5px', fontSize: '12px', borderRadius: '4px', border: '1px solid #ddd' }}
                      >
                        <option value="available">Available</option>
                        <option value="occupied">Occupied</option>
                        <option value="cleaning">Cleaning</option>
                        <option value="maintenance">Maintenance</option>
                        <option value="reserved">Reserved</option>
                      </select>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div style={{ textAlign: 'center', padding: '40px', color: '#666' }}>
            <p>No rooms found</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ReceptionistRooms;
