import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import '../styles/Auth.css';

const Register = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    role: 'guest',
    phone: '',
    address: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { register } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await register(formData);
    
    if (result.success) {
      navigate('/dashboard');
    } else {
      setError(result.message);
    }
    
    setLoading(false);
  };

  const getPasswordStrength = () => {
    const password = formData.password;
    if (password.length === 0) return { strength: 0, text: '', color: '' };
    if (password.length < 6) return { strength: 1, text: 'Weak', color: '#dc3545' };
    if (password.length < 10) return { strength: 2, text: 'Medium', color: '#ffc107' };
    return { strength: 3, text: 'Strong', color: '#28a745' };
  };

  const passwordStrength = getPasswordStrength();

  return (
    <div className="auth-container">
      <div className="auth-card" style={{ maxWidth: '600px' }}>
        <div className="welcome-badge">Join LuxuryStay</div>
        <h2>Create Account</h2>
        <p style={{ textAlign: 'center', color: 'var(--text-light)', marginBottom: '2rem' }}>
          Fill in your details to get started
        </p>

        {error && (
          <div className="alert alert-error" style={{ animation: 'shake 0.5s ease' }}>
            <strong>‚ö†Ô∏è</strong> {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
            <div className="form-group">
              <label htmlFor="firstName">
                <span>üë§</span> First Name
              </label>
              <input
                id="firstName"
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                placeholder="John"
                required
                autoComplete="given-name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="lastName">
                <span>üë§</span> Last Name
              </label>
              <input
                id="lastName"
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                placeholder="Doe"
                required
                autoComplete="family-name"
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="email">
              <span>üìß</span> Email Address
            </label>
            <input
              id="email"
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="john.doe@example.com"
              required
              autoComplete="email"
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">
              <span>üîí</span> Password
              {formData.password && (
                <span style={{ 
                  float: 'right', 
                  fontSize: '12px', 
                  color: passwordStrength.color,
                  fontWeight: '600'
                }}>
                  {passwordStrength.text}
                </span>
              )}
            </label>
            <div style={{ position: 'relative' }}>
              <input
                id="password"
                type={showPassword ? 'text' : 'password'}
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Minimum 6 characters"
                required
                minLength="6"
                autoComplete="new-password"
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                style={{
                  position: 'absolute',
                  right: '15px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '18px',
                  color: 'var(--text-light)',
                  padding: '5px'
                }}
              >
                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
            {formData.password && (
              <div style={{ 
                marginTop: '5px', 
                height: '4px', 
                background: '#e9ecef', 
                borderRadius: '2px',
                overflow: 'hidden'
              }}>
                <div style={{
                  height: '100%',
                  width: `${(passwordStrength.strength / 3) * 100}%`,
                  background: passwordStrength.color,
                  transition: 'all 0.3s ease'
                }}></div>
              </div>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="role">
              <span>üé≠</span> Role
            </label>
            <select 
              id="role"
              name="role" 
              value={formData.role} 
              onChange={handleChange} 
              required
            >
              <option value="guest">üë§ Guest</option>
              <option value="admin">üëë Admin</option>
              <option value="manager">üíº Manager</option>
              <option value="receptionist">üìû Receptionist</option>
              <option value="housekeeping">üßπ Housekeeping</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="phone">
              <span>üì±</span> Phone Number <span style={{ fontSize: '12px', color: 'var(--text-light)' }}>(Optional)</span>
            </label>
            <input
              id="phone"
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              placeholder="+1 (555) 123-4567"
              autoComplete="tel"
            />
          </div>

          <div className="form-group">
            <label htmlFor="address">
              <span>üìç</span> Address <span style={{ fontSize: '12px', color: 'var(--text-light)' }}>(Optional)</span>
            </label>
            <textarea
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
              placeholder="Enter your address"
              rows="3"
            />
          </div>

          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? (
              <>
                <span className="spinner-small"></span>
                Creating Account...
              </>
            ) : (
              <>
