import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext.jsx';
import NotificationBell from './NotificationBell.jsx';
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (!user) {
    return (
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-brand">
            <div className="logo-container">
              <div className="logo-icon">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
                  <polyline points="9 22 9 12 15 12 15 22"></polyline>
                </svg>
              </div>
              <span className="logo-text">
                <span className="logo-main">Luxury</span>
                <span className="logo-sub">Stay</span>
              </span>
            </div>
          </Link>
          <div className="navbar-links">
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </div>
        </div>
      </nav>
    );
  }

  // For receptionist, show receptionist-specific links
  if (user.role === 'receptionist') {
    return (
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/dashboard" className="navbar-brand">
            <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
              <polyline points="9 22 9 12 15 12 15 22"></polyline>
            </svg>
            <span>LuxuryStay</span>
          </Link>
          <div className="navbar-links">
            <Link to="/dashboard">Dashboard</Link>
            <Link to="/reservations">Reservations</Link>
            <Link to="/rooms">Rooms</Link>
            <NotificationBell />
            <span className="navbar-user">Welcome, {user.firstName} ({user.role})</span>
            <button onClick={handleLogout} className="btn btn-secondary">Logout</button>
          </div>
        </div>
      </nav>
    );
  }

  return (
    <nav className="navbar">
      <div className="navbar-container">
          <Link to="/dashboard" className="navbar-brand">
            <svg width="28" height="28" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M3 9l9-7 9 7v11a2 2 0 01-2 2H5a2 2 0 01-2-2z"></path>
              <polyline points="9 22 9 12 15 12 15 22"></polyline>
            </svg>
            <span>LuxuryStay</span>
          </Link>
        <div className="navbar-links">
          <Link to="/dashboard">Dashboard</Link>
          {['admin', 'manager'].includes(user.role) && (
            <Link to="/users">Users</Link>
          )}
          {['admin', 'manager', 'guest'].includes(user.role) && (
            <Link to="/rooms">Rooms</Link>
          )}
          {['admin', 'manager', 'guest'].includes(user.role) && (
            <Link to="/reservations">Reservations</Link>
          )}
          {['admin', 'manager', 'guest'].includes(user.role) && (
            <Link to="/billing">Billing</Link>
          )}
          {['admin', 'manager', 'housekeeping'].includes(user.role) && (
            <Link to="/housekeeping">Housekeeping</Link>
          )}
          {['admin', 'manager', 'guest'].includes(user.role) && (
            <Link to="/maintenance">Maintenance</Link>
          )}
          {['admin', 'manager'].includes(user.role) && (
            <Link to="/reports">Reports</Link>
          )}
          {user.role === 'admin' && (
            <Link to="/settings">Settings</Link>
          )}
          {['admin', 'manager'].includes(user.role) && (
            <Link to="/admin/gallery">Gallery Management</Link>
          )}
          {['admin', 'manager', 'guest'].includes(user.role) && (
            <Link to="/feedback">Feedback</Link>
          )}
          {['admin', 'manager', 'guest'].includes(user.role) && (
            <Link to="/services">Services</Link>
          )}
          {user.role === 'guest' && (
            <Link to="/gallery">Gallery</Link>
          )}
          {user.role === 'guest' && (
            <Link to="/about">About Us</Link>
          )}
          <NotificationBell />
          <span className="navbar-user">Welcome, {user.firstName} ({user.role})</span>
          <button onClick={handleLogout} className="btn btn-secondary">Logout</button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
