import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import axios from 'axios';
import GuestFooter from '../../components/GuestFooter.jsx';

const RoomDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [room, setRoom] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedImageIndex, setSelectedImageIndex] = useState(0);
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [numberOfGuests, setNumberOfGuests] = useState(1);
  const [isBookingCardSticky, setIsBookingCardSticky] = useState(false);

  useEffect(() => {
    fetchRoomDetails();
  }, [id]);

  useEffect(() => {
    const handleScroll = () => {
      setIsBookingCardSticky(window.scrollY > 100);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const fetchRoomDetails = async () => {
    try {
      const response = await axios.get(`/api/rooms/${id}`);
      setRoom(response.data.room);
    } catch (error) {
      console.error('Error fetching room details:', error);
      alert('Room not found');
      navigate('/rooms');
    } finally {
      setLoading(false);
    }
  };

  const handleBookNow = () => {
    if (!checkInDate || !checkOutDate) {
      alert('Please select check-in and check-out dates');
      return;
    }
    const checkIn = new Date(checkInDate);
    const checkOut = new Date(checkOutDate);
    if (checkOut <= checkIn) {
      alert('Check-out date must be after check-in date');
      return;
    }
    navigate('/reservations', {
      state: { roomId: room._id, checkInDate, checkOutDate, numberOfGuests }
    });
  };

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #1a1410 0%, #2d2520 100%)'
      }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{
            width: '80px',
            height: '80px',
            border: '4px solid rgba(199, 146, 91, 0.2)',
            borderTop: '4px solid #c7925b',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 24px'
          }} />
          <style>{`@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }`}</style>
          <p style={{ color: '#c7925b', fontSize: '18px', fontWeight: '500' }}>Loading luxury experience...</p>
        </div>
      </div>
    );
  }

  if (!room) {
    return (
      <div style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #1a1410 0%, #2d2520 100%)'
      }}>
        <div style={{ textAlign: 'center', color: '#fff' }}>
          <div style={{ fontSize: '72px', marginBottom: '24px' }}>ðŸ¨</div>
          <h2 style={{ fontSize: '32px', marginBottom: '16px' }}>Room not found</h2>
          <Link to="/rooms" style={{
            display: 'inline-block',
            padding: '14px 32px',
            background: 'linear-gradient(135deg, #c7925b 0%, #8b6f47 100%)',
            color: '#fff',
            textDecoration: 'none',
            borderRadius: '12px',
            fontSize: '16px',
            fontWeight: '600',
            transition: 'all 0.3s ease'
          }}>
            Back to Rooms
          </Link>
        </div>
      </div>
    );
  }

  const nights = checkInDate && checkOutDate 
    ? Math.ceil((new Date(checkOutDate) - new Date(checkInDate)) / (1000 * 60 * 60 * 24)) 
    : 1;
  const totalPrice = room.pricePerNight * nights;

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0f0d0a 0%, #1a1410 50%, #2d2520 100%)',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated background elements */}
      <div style={{
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        opacity: 0.03,
        backgroundImage: 'radial-gradient(circle at 20% 50%, #c7925b 0%, transparent 50%), radial-gradient(circle at 80% 80%, #d4af37 0%, transparent 50%)',
        pointerEvents: 'none'
      }} />

      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '0 24px' }}>
        {/* Enhanced Breadcrumb */}
        <div style={{
          padding: '32px 0 24px',
          fontSize: '14px',
          color: 'rgba(255, 255, 255, 0.6)',
          fontWeight: '500',
          letterSpacing: '0.5px'
        }}>
          <Link to="/" style={{
            color: '#c7925b',
            textDecoration: 'none',
            transition: 'all 0.3s ease',
            position: 'relative'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.color = '#d4af37';
            e.currentTarget.style.transform = 'translateX(2px)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.color = '#c7925b';
            e.currentTarget.style.transform = 'translateX(0)';
          }}>
            Home
          </Link>
          <span style={{ margin: '0 12px', color: 'rgba(212, 175, 55, 0.4)' }}>â†’</span>
          <Link to="/rooms" style={{
            color: '#c7925b',
            textDecoration: 'none',
            transition: 'all 0.3s ease'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.color = '#d4af37';
            e.currentTarget.style.transform = 'translateX(2px)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.color = '#c7925b';
            e.currentTarget.style.transform = 'translateX(0)';
          }}>
            Rooms
          </Link>
          <span style={{ margin: '0 12px', color: 'rgba(212, 175, 55, 0.4)' }}>â†’</span>
          <span style={{ color: 'rgba(255, 255, 255, 0.9)' }}>Room {room.roomNumber}</span>
        </div>

        {/* Enhanced Room Header */}
        <div style={{
          marginBottom: '48px',
          padding: '40px',
          background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.1) 0%, rgba(139, 111, 71, 0.05) 100%)',
          borderRadius: '24px',
          border: '1px solid rgba(212, 175, 55, 0.2)',
          position: 'relative',
          overflow: 'hidden'
        }}>
          <div style={{
            position: 'absolute',
            top: '-50%',
            right: '-10%',
            width: '400px',
            height: '400px',
            background: 'radial-gradient(circle, rgba(212, 175, 55, 0.1) 0%, transparent 70%)',
            pointerEvents: 'none'
          }} />
          
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', position: 'relative', zIndex: 1 }}>
            <div>
              <div style={{
                display: 'inline-block',
                padding: '8px 20px',
                background: 'linear-gradient(135deg, rgba(212, 175, 55, 0.2) 0%, rgba(199, 146, 91, 0.15) 100%)',
                borderRadius: '30px',
                border: '1px solid rgba(212, 175, 55, 0.3)',
                marginBottom: '20px',
                fontSize: '13px',
                fontWeight: '600',
                color: '#d4af37',
                letterSpacing: '1px',
                textTransform: 'uppercase'
              }}>
                {room.roomType}
              </div>
              
              <h1 style={{
                fontSize: '56px',
                fontWeight: '800',
                background: 'linear-gradient(135deg, #fff 0%, rgba(255, 255, 255, 0.8) 100%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                marginBottom: '24px',
                letterSpacing: '-1px'
              }}>
                Room {room.roomNumber}
              </h1>
              
              <div style={{
                display: 'flex',
                gap: '24px',
                fontSize: '16px',
                color: 'rgba(255, 255, 255, 0.7)',
                marginBottom: '16px'
              }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px',
                  padding: '12px 20px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '12px',
                  backdropFilter: 'blur(10px)'
                }}>
                  <span style={{ fontSize: '20px' }}>ðŸ¢</span>
                  <span style={{ fontWeight: '500' }}>Floor {room.floor}</span>
                </div>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '10px',
                  padding: '12px 20px',
                  background: 'rgba(255, 255, 255, 0.05)',
                  borderRadius: '12px',
                  backdropFilter: 'blur(10px)'
                }}>
                  <span style={{ fontSize: '20px' }}>ðŸ‘¥</span>
                  <span style={{ fontWeight: '500' }}>Up to {room.maxOccupancy} Guests</span>
                </div>
              </div>
              
              <div style={{
                display: 'inline-block',
                padding: '10px 20px',
                background: room.status === 'available' 
                  ? 'linear-gradient(135deg, rgba(16, 185, 129, 0.2) 0%, rgba(5, 150, 105, 0.15) 100%)'
                  : 'linear-gradient(135deg, rgba(239, 68, 68, 0.2) 0%, rgba(220, 38, 38, 0.15) 100%)',
                borderRadius: '10px',
                border: `1px solid ${room.status === 'available' ? 'rgba(16, 185, 129, 0.3)' : 'rgba(239, 68, 68, 0.3)'}`,
                fontSize: '14px',
                fontWeight: '600',
                color: room.status === 'available' ? '#10b981' : '#ef4444',
                textTransform: 'uppercase',
                letterSpacing: '0.5px'
              }}>
                {room.status}
              </div>
            </div>
            
            <div style={{ textAlign: 'right' }}>
              <div style={{
                fontSize: '52px',
                fontWeight: '800',
                background: 'linear-gradient(135deg, #d4af37 0%, #c7925b 100%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                lineHeight: '1',
                marginBottom: '8px'
              }}>
                ${room.pricePerNight.toFixed(2)}
              </div>
              <div style={{
                fontSize: '16px',
                color: 'rgba(255, 255, 255, 0.6)',
                fontWeight: '500',
                letterSpacing: '0.5px'
              }}>
                per night
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Image Gallery */}
        {room.images && room.images.length > 0 ? (
          <div style={{ marginBottom: '64px' }}>
            <div style={{
              display: 'grid',
              gridTemplateColumns: room.images.length > 1 ? '2fr 1fr 1fr' : '1fr',
              gap: '16px',
              height: '600px',
              borderRadius: '24px',
              overflow: 'hidden',
              position: 'relative'
            }}>
              {/* Main Image with overlay gradient */}
              <div style={{
                position: 'relative',
                overflow: 'hidden',
                borderRadius: '24px',
                boxShadow: '0 20px 60px rgba(0, 0, 0, 0.5)',
                border: '2px solid rgba(212, 175, 55, 0.2)'
              }}
              onClick={() => setSelectedImageIndex(0)}
              onMouseEnter={(e) => {
                const img = e.currentTarget.querySelector('img');
                if (img) img.style.transform = 'scale(1.08)';
                e.currentTarget.style.boxShadow = '0 25px 70px rgba(212, 175, 55, 0.4)';
              }}
              onMouseLeave={(e) => {
                const img = e.currentTarget.querySelector('img');
                if (img) img.style.transform = 'scale(1)';
                e.currentTarget.style.boxShadow = '0 20px 60px rgba(0, 0, 0, 0.5)';
              }}>
                <img
                  src={room.images[selectedImageIndex]}
                  alt="Room"
                  style={{
                    width: '100%',
                    height: '100%',
                    objectFit: 'cover',
                    transition: 'transform 0.6s cubic-bezier(0.4, 0, 0.2, 1)',
                    cursor: 'pointer'
                  }}
                />
                <div style={{
                  position: 'absolute',
                  bottom: 0,
                  left: 0,
                  right: 0,
                  height: '40%',
                  background: 'linear-gradient(to top, rgba(0, 0, 0, 0.7) 0%, transparent 100%)',
                  pointerEvents: 'none'
                }} />
                
                {/* Image indicators */}
                {room.images.length > 1 && (
                  <div style={{
                    position: 'absolute',
                    bottom: '24px',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    display: 'flex',
                    gap: '8px',
                    zIndex: 2
                  }}>
                    {room.images.map((_, index) => (
                      <button
                        key={index}
                        onClick={(e) => {
                          e.stopPropagation();
                          setSelectedImageIndex(index);
                        }}
                        style={{
                          width: index === selectedImageIndex ? '32px' : '10px',
                          height: '10px',
                          borderRadius: '5px',
                          border: 'none',
                          background: index === selectedImageIndex 
                            ? 'linear-gradient(135deg, #d4af37 0%, #c7925b 100%)' 
                            : 'rgba(255,255,255,0.5)',
                          cursor: 'pointer',
                          transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                          boxShadow: index === selectedImageIndex ? '0 4px 12px rgba(212, 175, 55, 0.6)' : 'none'
                        }}
                      />
                    ))}
                  </div>
                )}
              </div>

              {/* Thumbnail Grid */}
              {room.images.length > 1 && (
                <>
                  {room.images.slice(1, 3).map((image, index) => (
                    <div
                      key={index}
                      style={{
                        position: 'relative',
                        overflow: 'hidden',
                        borderRadius: '20px',
                        cursor: 'pointer',
                        boxShadow: selectedImageIndex === index + 1 
                          ? '0 12px 30px rgba(212, 175, 55, 0.4)' 
                          : '0 8px 20px rgba(0, 0, 0, 0.3)',
                        border: selectedImageIndex === index + 1 
                          ? '3px solid #d4af37' 
                          : '2px solid rgba(212, 175, 55, 0.1)',
                        transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
                      }}
                      onClick={() => setSelectedImageIndex(index + 1)}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.transform = 'scale(1.03)';
                        e.currentTarget.style.boxShadow = '0 15px 40px rgba(212, 175, 55, 0.5)';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.transform = 'scale(1)';
                        e.currentTarget.style.boxShadow = selectedImageIndex === index + 1 
                          ? '0 12px 30px rgba(212, 175, 55, 0.4)' 
                          : '0 8px 20px rgba(0, 0, 0, 0.3)';
                      }}
                    >
                      <img
                        src={image}
                        alt={`Room view ${index + 2}`}
                        style={{
                          width: '100%',
                          height: '100%',
                          objectFit: 'cover',
                          transition: 'opacity 0.3s ease'
                        }}
                      />
                      {selectedImageIndex === index + 1 && (
                        <div style={{
                          position: 'absolute',
                          top: '12px',
                          right: '12px',
                          width: '36px',
                          height: '36px',
                          borderRadius: '50%',
                          background: 'linear-gradient(135deg, #d4af37 0%, #c7925b 100%)',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          color: '#fff',
                          fontSize: '18px',
                          fontWeight: 'bold',
                          boxShadow: '0 4px 12px rgba(212, 175, 55, 0.5)'
                        }}>
                          âœ“
                        </div>
                      )}
                    </div>
                  ))}
                </>
              )}
            </div>

            {/* Additional thumbnails row */}
            {room.images.length > 3 && (
              <div style={{
                display: 'flex',
                gap: '12px',
                marginTop: '16px',
                overflowX: 'auto',
                paddingBottom: '8px'
              }}>
                {room.images.slice(3).map((image, index) => (
                  <div
                    key={index}
                    style={{
                      minWidth: '120px',
                      height: '90px',
                      borderRadius: '12px',
                      overflow: 'hidden',
                      cursor: 'pointer',
                      opacity: selectedImageIndex === index + 3 ? 1 : 0.6,
                      border: selectedImageIndex === index + 3 ? '2px solid #d4af37' : '2px solid transparent',
                      transition: 'all 0.3s ease',
                      boxShadow: selectedImageIndex === index + 3 ? '0 6px 16px rgba(212, 175, 55, 0.4)' : 'none'
                    }}
                    onClick={() => setSelectedImageIndex(index + 3)}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.opacity = '1';
                      e.currentTarget.style.transform = 'translateY(-4px)';
                    }}
                    onMouseLeave={(e) => {
                      if (selectedImageIndex !== index + 3) {
                        e.currentTarget.style.opacity = '0.6';
                      }
                      e.currentTarget.style.transform = 'translateY(0)';
                    }}
                  >
                    <img
                      src={image}
                      alt={`Room view ${index + 4}`}
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover'
                      }}
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        ) : (
          <div style={{
            height: '600px',
            background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.1) 0%, rgba(139, 111, 71, 0.05) 100%)',
            borderRadius: '24px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            marginBottom: '64px',
            border: '2px dashed rgba(212, 175, 55, 0.3)'
          }}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '80px', marginBottom: '16px' }}>ðŸ›ï¸</div>
              <p style={{ color: 'rgba(255, 255, 255, 0.6)', fontSize: '18px' }}>No Image Available</p>
            </div>
          </div>
        )}

        {/* Content Grid */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: '1fr 480px',
          gap: '48px',
          paddingBottom: '80px'
        }}>
          {/* Left Column */}
          <div>
            {/* Description */}
            <div style={{
              background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.08) 0%, rgba(139, 111, 71, 0.04) 100%)',
              padding: '48px',
              borderRadius: '24px',
              marginBottom: '32px',
              border: '1px solid rgba(212, 175, 55, 0.15)',
              position: 'relative',
              overflow: 'hidden'
            }}>
              <div style={{
                position: 'absolute',
                top: '-20px',
                left: '-20px',
                width: '100px',
                height: '100px',
                background: 'radial-gradient(circle, rgba(212, 175, 55, 0.15) 0%, transparent 70%)',
                pointerEvents: 'none'
              }} />
              
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '16px',
                marginBottom: '24px'
              }}>
                <div style={{
                  width: '56px',
                  height: '56px',
                  borderRadius: '16px',
                  background: 'linear-gradient(135deg, rgba(212, 175, 55, 0.2) 0%, rgba(199, 146, 91, 0.15) 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '28px'
                }}>
                  âœ¨
                </div>
                <h2 style={{
                  fontSize: '32px',
                  fontWeight: '700',
                  color: '#fff',
                  margin: 0
                }}>
                  About this room
                </h2>
              </div>
              
              <p style={{
                fontSize: '17px',
                lineHeight: '1.8',
                color: 'rgba(255, 255, 255, 0.8)',
                margin: 0
              }}>
                {room.description || 'Experience luxury and comfort in this beautifully designed room with premium amenities and breathtaking views.'}
              </p>
            </div>

            {/* Amenities */}
            {room.amenities && room.amenities.length > 0 && (
              <div style={{
                background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.08) 0%, rgba(139, 111, 71, 0.04) 100%)',
                padding: '48px',
                borderRadius: '24px',
                marginBottom: '32px',
                border: '1px solid rgba(212, 175, 55, 0.15)'
              }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '16px',
                  marginBottom: '32px'
                }}>
                  <div style={{
                    width: '56px',
                    height: '56px',
                    borderRadius: '16px',
                    background: 'linear-gradient(135deg, rgba(212, 175, 55, 0.2) 0%, rgba(199, 146, 91, 0.15) 100%)',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '28px'
                  }}>
                    ðŸ†
                  </div>
                  <h2 style={{
                    fontSize: '32px',
                    fontWeight: '700',
                    color: '#fff',
                    margin: 0
                  }}>
                    What this place offers
                  </h2>
                </div>
                
                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
                  gap: '16px'
                }}>
                  {room.amenities.map((amenity, index) => (
                    <div
                      key={index}
                      style={{
                        padding: '20px 24px',
                        background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.05) 0%, rgba(139, 111, 71, 0.02) 100%)',
                        borderRadius: '16px',
                        border: '1px solid rgba(212, 175, 55, 0.1)',
                        transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                        cursor: 'default'
                      }}
                      onMouseEnter={(e) => {
                        e.currentTarget.style.background = 'linear-gradient(135deg, rgba(199, 146, 91, 0.15) 0%, rgba(139, 111, 71, 0.08) 100%)';
                        e.currentTarget.style.transform = 'translateY(-4px)';
                        e.currentTarget.style.boxShadow = '0 8px 24px rgba(199, 146, 91, 0.2)';
                        e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.3)';
                      }}
                      onMouseLeave={(e) => {
                        e.currentTarget.style.background = 'linear-gradient(135deg, rgba(199, 146, 91, 0.05) 0%, rgba(139, 111, 71, 0.02) 100%)';
                        e.currentTarget.style.transform = 'translateY(0)';
                        e.currentTarget.style.boxShadow = 'none';
                        e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.1)';
                      }}
                    >
                      <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
                        <div style={{
                          width: '32px',
                          height: '32px',
                          borderRadius: '8px',
                          background: 'linear-gradient(135deg, rgba(212, 175, 55, 0.2) 0%, rgba(199, 146, 91, 0.15) 100%)',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          fontSize: '14px',
                          fontWeight: 'bold',
                          color: '#d4af37'
                        }}>
                          âœ“
                        </div>
                        <span style={{
                          fontSize: '16px',
                          fontWeight: '500',
                          color: 'rgba(255, 255, 255, 0.9)'
                        }}>
                          {amenity}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Room Info */}
            <div style={{
              background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.08) 0%, rgba(139, 111, 71, 0.04) 100%)',
              padding: '48px',
              borderRadius: '24px',
              border: '1px solid rgba(212, 175, 55, 0.15)'
            }}>
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '16px',
                marginBottom: '32px'
              }}>
                <div style={{
                  width: '56px',
                  height: '56px',
                  borderRadius: '16px',
                  background: 'linear-gradient(135deg, rgba(212, 175, 55, 0.2) 0%, rgba(199, 146, 91, 0.15) 100%)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '28px'
                }}>
                  â„¹ï¸
                </div>
                <h2 style={{
                  fontSize: '32px',
                  fontWeight: '700',
                  color: '#fff',
                  margin: 0
                }}>
                  Room Information
                </h2>
              </div>
              
              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(2, 1fr)',
                gap: '16px'
              }}>
                {[
                  { label: 'Room Number', value: room.roomNumber, icon: 'ðŸ”¢' },
                  { label: 'Floor', value: `Floor ${room.floor}`, icon: 'ðŸ¢' },
                  { label: 'Room Type', value: room.roomType, icon: 'ðŸ¨' },
                  { label: 'Max Occupancy', value: `${room.maxOccupancy} Guests`, icon: 'ðŸ‘¥' }
                ].map((item, index) => (
                  <div
                    key={index}
                    style={{
                      padding: '24px',
                      background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.05) 0%, rgba(139, 111, 71, 0.02) 100%)',
                      borderRadius: '16px',
                      border: '1px solid rgba(212, 175, 55, 0.1)',
                      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                      cursor: 'default'
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.background = 'linear-gradient(135deg, rgba(199, 146, 91, 0.12) 0%, rgba(139, 111, 71, 0.06) 100%)';
                      e.currentTarget.style.transform = 'translateY(-4px)';
                      e.currentTarget.style.boxShadow = '0 8px 24px rgba(199, 146, 91, 0.2)';
                      e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.3)';
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.background = 'linear-gradient(135deg, rgba(199, 146, 91, 0.05) 0%, rgba(139, 111, 71, 0.02) 100%)';
                      e.currentTarget.style.transform = 'translateY(0)';
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.1)';
                    }}
                  >
                    <div style={{
                      fontSize: '14px',
                      color: 'rgba(255, 255, 255, 0.5)',
                      marginBottom: '8px',
                      fontWeight: '500',
                      textTransform: 'uppercase',
                      letterSpacing: '0.5px'
                    }}>
                      {item.label}
                    </div>
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      gap: '12px'
                    }}>
                      <span style={{ fontSize: '24px' }}>{item.icon}</span>
                      <span style={{
                        fontSize: '18px',
                        fontWeight: '600',
                        color: 'rgba(255, 255, 255, 0.95)'
                      }}>
                        {item.value}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Right Column - Booking Card */}
          <div>
            <div style={{
              position: isBookingCardSticky ? 'sticky' : 'relative',
              top: isBookingCardSticky ? '24px' : '0',
              background: 'linear-gradient(135deg, rgba(199, 146, 91, 0.12) 0%, rgba(139, 111, 71, 0.08) 100%)',
              padding: '40px',
              borderRadius: '24px',
              border: '2px solid rgba(212, 175, 55, 0.2)',
              boxShadow: '0 20px 60px rgba(0, 0, 0, 0.4)',
              backdropFilter: 'blur(20px)',
              transition: 'all 0.5s cubic-bezier(0.4, 0, 0.2, 1)'
            }}>
              {/* Price Header */}
              <div style={{
                textAlign: 'center',
                marginBottom: '32px',
                paddingBottom: '32px',
                borderBottom: '1px solid rgba(212, 175, 55, 0.2)'
              }}>
                <div style={{
                  fontSize: '48px',
                  fontWeight: '800',
                  background: 'linear-gradient(135deg, #d4af37 0%, #c7925b 100%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  lineHeight: '1',
                  marginBottom: '8px'
                }}>
                  ${room.pricePerNight.toFixed(2)}
                </div>
                <div style={{
                  fontSize: '16px',
                  color: 'rgba(255, 255, 255, 0.6)',
                  fontWeight: '500',
                  letterSpacing: '0.5px'
                }}>
                  per night
                </div>
              </div>

              {/* Booking Form */}
              <div style={{ marginBottom: '32px' }}>
                {/* Check-in */}
                <div style={{ marginBottom: '20px' }}>
                  <label style={{
                    display: 'block',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: 'rgba(255, 255, 255, 0.8)',
                    marginBottom: '10px',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Check-in
                  </label>
                  <input
                    type="date"
                    value={checkInDate}
                    onChange={(e) => setCheckInDate(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                    style={{
                      width: '100%',
                      padding: '16px 18px',
                      border: '2px solid rgba(212, 175, 55, 0.2)',
                      borderRadius: '14px',
                      fontSize: '16px',
                      background: 'rgba(0, 0, 0, 0.3)',
                      color: '#fff',
                      transition: 'all 0.3s ease',
                      outline: 'none',
                      fontWeight: '500'
                    }}
                    onFocus={(e) => {
                      e.currentTarget.style.borderColor = '#d4af37';
                      e.currentTarget.style.boxShadow = '0 0 0 4px rgba(212, 175, 55, 0.15)';
                      e.currentTarget.style.background = 'rgba(0, 0, 0, 0.4)';
                    }}
                    onBlur={(e) => {
                      e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.2)';
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.background = 'rgba(0, 0, 0, 0.3)';
                    }}
                  />
                </div>

                {/* Check-out */}
                <div style={{ marginBottom: '20px' }}>
                  <label style={{
                    display: 'block',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: 'rgba(255, 255, 255, 0.8)',
                    marginBottom: '10px',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Check-out
                  </label>
                  <input
                    type="date"
                    value={checkOutDate}
                    onChange={(e) => setCheckOutDate(e.target.value)}
                    min={checkInDate || new Date().toISOString().split('T')[0]}
                    style={{
                      width: '100%',
                      padding: '16px 18px',
                      border: '2px solid rgba(212, 175, 55, 0.2)',
                      borderRadius: '14px',
                      fontSize: '16px',
                      background: 'rgba(0, 0, 0, 0.3)',
                      color: '#fff',
                      transition: 'all 0.3s ease',
                      outline: 'none',
                      fontWeight: '500'
                    }}
                    onFocus={(e) => {
                      e.currentTarget.style.borderColor = '#d4af37';
                      e.currentTarget.style.boxShadow = '0 0 0 4px rgba(212, 175, 55, 0.15)';
                      e.currentTarget.style.background = 'rgba(0, 0, 0, 0.4)';
                    }}
                    onBlur={(e) => {
                      e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.2)';
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.background = 'rgba(0, 0, 0, 0.3)';
                    }}
                  />
                </div>

                {/* Guests */}
                <div>
                  <label style={{
                    display: 'block',
                    fontSize: '14px',
                    fontWeight: '600',
                    color: 'rgba(255, 255, 255, 0.8)',
                    marginBottom: '10px',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Guests
                  </label>
                  <select
                    value={numberOfGuests}
                    onChange={(e) => setNumberOfGuests(parseInt(e.target.value))}
                    style={{
                      width: '100%',
                      padding: '16px 18px',
                      border: '2px solid rgba(212, 175, 55, 0.2)',
                      borderRadius: '14px',
                      fontSize: '16px',
                      background: 'rgba(0, 0, 0, 0.3)',
                      color: '#fff',
                      transition: 'all 0.3s ease',
                      outline: 'none',
                      cursor: 'pointer',
                      fontWeight: '500'
                    }}
                    onFocus={(e) => {
                      e.currentTarget.style.borderColor = '#d4af37';
                      e.currentTarget.style.boxShadow = '0 0 0 4px rgba(212, 175, 55, 0.15)';
                      e.currentTarget.style.background = 'rgba(0, 0, 0, 0.4)';
                    }}
                    onBlur={(e) => {
                      e.currentTarget.style.borderColor = 'rgba(212, 175, 55, 0.2)';
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.background = 'rgba(0, 0, 0, 0.3)';
                    }}
                  >
                    {Array.from({ length: room.maxOccupancy }, (_, i) => i + 1).map(num => (
                      <option key={num} value={num} style={{ background: '#1a1410', color: '#fff' }}>
                        {num} {num === 1 ? 'Guest' : 'Guests'}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Price Breakdown */}
              {checkInDate && checkOutDate && (
                <div style={{
                  marginBottom: '24px',
                  padding: '24px',
                  background: 'rgba(0, 0, 0, 0.3)',
                  borderRadius: '16px',
                  border: '1px solid rgba(212, 175, 55, 0.15)'
                }}>
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    marginBottom: '12px',
                    fontSize: '15px',
                    color: 'rgba(255, 255, 255, 0.7)'
                  }}>
                    <span>${room.pricePerNight.toFixed(2)} Ã— {nights} {nights === 1 ? 'night' : 'nights'}</span>
                    <span style={{ fontWeight: '600' }}>${(room.pricePerNight * nights).toFixed(2)}</span>
                  </div>
                  <div style={{
                    paddingTop: '16px',
                    borderTop: '1px solid rgba(212, 175, 55, 0.2)',
                    display: 'flex',
                    justifyContent: 'space-between',
                    fontSize: '20px',
                    fontWeight: '700',
                    color: '#d4af37'
                  }}>
                    <span>Total</span>
                    <span>${totalPrice.toFixed(2)}</span>
                  </div>
                </div>
              )}

              {/* Book Now Button */}
              <button
                onClick={handleBookNow}
                style={{
                  width: '100%',
                  padding: '18px',
                  background: 'linear-gradient(135deg, #d4af37 0%, #c7925b 100%)',
                  border: 'none',
                  borderRadius: '14px',
                  fontSize: '18px',
                  fontWeight: '700',
                  color: '#fff',
                  cursor: 'pointer',
                  transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
                  boxShadow: '0 8px 24px rgba(212, 175, 55, 0.4)',
                  textTransform: 'uppercase',
                  letterSpacing: '1px',
                  position: 'relative',
                  overflow: 'hidden'
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.background = 'linear-gradient(135deg, #e5c158 0%, #9d7f58 100%)';
                  e.currentTarget.style.transform = 'translateY(-3px)';
                  e.currentTarget.style.boxShadow = '0 12px 32px rgba(212, 175, 55, 0.6)';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.background = 'linear-gradient(135deg, #d4af37 0%, #c7925b 100%)';
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 8px 24px rgba(212, 175, 55, 0.4)';
                }}
              >
                âœ¨ Reserve Now âœ¨
              </button>

              <div style={{
                marginTop: '16px',
                textAlign: 'center',
                fontSize: '14px',
                color: 'rgba(255, 255, 255, 0.5)',
                fontWeight: '500'
              }}>
                ðŸ’³ You won't be charged yet
              </div>
            </div>
          </div>
        </div>
      </div>

      <GuestFooter />
    </div>
  );
};

export default RoomDetails;
