import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../../context/AuthContext.jsx';

const ReceptionistDashboard = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalRooms: 0,
    availableRooms: 0,
    occupiedRooms: 0,
    cleaningRooms: 0,
    pendingReservations: 0,
    checkedInGuests: 0
  });
  const [loading, setLoading] = useState(true);
  const [todayReservations, setTodayReservations] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      // Fetch today's date
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      const tomorrow = new Date(today);
      tomorrow.setDate(tomorrow.getDate() + 1);

      // Fetch reservations for today
      const reservationsResponse = await axios.get('/api/reservations');
      const allReservations = reservationsResponse.data.reservations || [];
      
      // Filter today's check-ins and check-outs
      const todayCheckIns = allReservations.filter(r => {
        const checkInDate = new Date(r.checkInDate);
        checkInDate.setHours(0, 0, 0, 0);
        return checkInDate.getTime() === today.getTime() && r.status === 'confirmed';
      });

      const todayCheckOuts = allReservations.filter(r => {
        const checkOutDate = new Date(r.checkOutDate);
        checkOutDate.setHours(0, 0, 0, 0);
        return checkOutDate.getTime() === today.getTime() && r.status === 'checked-in';
      });

      const checkedInGuests = allReservations.filter(r => r.status === 'checked-in').length;
      const pendingReservations = allReservations.filter(r => r.status === 'pending' || r.status === 'confirmed').length;

      // Fetch rooms
      const roomsResponse = await axios.get('/api/rooms');
      const allRooms = roomsResponse.data.rooms || [];
      
      const availableRooms = allRooms.filter(r => r.status === 'available').length;
      const occupiedRooms = allRooms.filter(r => r.status === 'occupied').length;
      const cleaningRooms = allRooms.filter(r => r.status === 'cleaning').length;

      // Get today's reservations (check-ins and check-outs)
      const todayRes = allReservations.filter(r => {
        const checkIn = new Date(r.checkInDate);
        checkIn.setHours(0, 0, 0, 0);
        const checkOut = new Date(r.checkOutDate);
        checkOut.setHours(0, 0, 0, 0);
        return (checkIn.getTime() === today.getTime() || checkOut.getTime() === today.getTime()) &&
               ['confirmed', 'checked-in'].includes(r.status);
      }).slice(0, 5);

      setStats({
        todayCheckIns: todayCheckIns.length,
        todayCheckOuts: todayCheckOuts.length,
        totalRooms: allRooms.length,
        availableRooms,
        occupiedRooms,
        cleaningRooms,
        pendingReservations,
        checkedInGuests
      });

      setTodayReservations(todayRes);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <div>
          <h1>Receptionist Dashboard</h1>
          <p style={{ color: '#666', marginTop: '5px' }}>Welcome, {user?.firstName}! Manage check-ins, check-outs, and room status</p>
        </div>
        <button onClick={handleLogout} className="btn btn-danger" style={{ marginLeft: 'auto' }}>
          Logout
        </button>
      </div>

      {/* Statistics Cards */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px', marginTop: '20px' }}>
        <div className="card" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', color: 'white' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Today's Check-ins</h3>
          <p style={{ fontSize: '36px', fontWeight: 'bold', margin: '0' }}>{stats.todayCheckIns}</p>
          <p style={{ marginTop: '5px', opacity: 0.9 }}>Reservations scheduled</p>
        </div>

        <div className="card" style={{ background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)', color: 'white' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Today's Check-outs</h3>
          <p style={{ fontSize: '36px', fontWeight: 'bold', margin: '0' }}>{stats.todayCheckOuts}</p>
          <p style={{ marginTop: '5px', opacity: 0.9 }}>Guests leaving today</p>
        </div>

        <div className="card" style={{ background: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)', color: 'white' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Checked-in Guests</h3>
          <p style={{ fontSize: '36px', fontWeight: 'bold', margin: '0' }}>{stats.checkedInGuests}</p>
          <p style={{ marginTop: '5px', opacity: 0.9 }}>Currently in hotel</p>
        </div>

        <div className="card" style={{ background: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)', color: 'white' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Available Rooms</h3>
          <p style={{ fontSize: '36px', fontWeight: 'bold', margin: '0' }}>{stats.availableRooms}</p>
          <p style={{ marginTop: '5px', opacity: 0.9 }}>Out of {stats.totalRooms} total</p>
        </div>

        <div className="card" style={{ background: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)', color: 'white' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Occupied Rooms</h3>
          <p style={{ fontSize: '36px', fontWeight: 'bold', margin: '0' }}>{stats.occupiedRooms}</p>
          <p style={{ marginTop: '5px', opacity: 0.9 }}>Currently in use</p>
        </div>

        <div className="card" style={{ background: 'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)', color: '#333' }}>
          <h3 style={{ marginBottom: '10px' }}>Rooms Cleaning</h3>
          <p style={{ fontSize: '36px', fontWeight: 'bold', margin: '0', color: '#d9534f' }}>{stats.cleaningRooms}</p>
          <p style={{ marginTop: '5px' }}>Need housekeeping</p>
        </div>
      </div>

      {/* Today's Reservations */}
      <div className="card" style={{ marginTop: '20px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
          <h2>Today's Reservations</h2>
          <button className="btn btn-primary" onClick={() => navigate('/reservations')}>
            View All Reservations
          </button>
        </div>
        {todayReservations.length > 0 ? (
          <table className="table" style={{ marginTop: '15px' }}>
            <thead>
              <tr>
                <th>Confirmation #</th>
                <th>Guest Name</th>
                <th>Room</th>
                <th>Check-in</th>
                <th>Check-out</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {todayReservations.map(reservation => (
                <tr key={reservation._id}>
                  <td>{reservation.confirmationNumber}</td>
                  <td>{reservation.guestId?.firstName} {reservation.guestId?.lastName}</td>
                  <td>{reservation.roomId?.roomNumber} - {reservation.roomId?.roomType}</td>
                  <td>{new Date(reservation.checkInDate).toLocaleDateString()}</td>
                  <td>{new Date(reservation.checkOutDate).toLocaleDateString()}</td>
                  <td>
                    <span className={`badge ${
                      reservation.status === 'confirmed' ? 'badge-success' :
                      reservation.status === 'checked-in' ? 'badge-info' :
                      'badge-warning'
                    }`}>
                      {reservation.status}
                    </span>
                  </td>
                  <td>
                    {reservation.status === 'confirmed' && (
                      <button
                        className="btn btn-success"
                        onClick={() => navigate(`/reservations`)}
                        style={{ padding: '5px 10px', fontSize: '12px' }}
                      >
                        Check-in
                      </button>
                    )}
                    {reservation.status === 'checked-in' && (
                      <button
                        className="btn btn-primary"
                        onClick={() => navigate(`/reservations`)}
                        style={{ padding: '5px 10px', fontSize: '12px' }}
                      >
                        Check-out
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p style={{ marginTop: '15px', color: '#666' }}>No reservations scheduled for today</p>
        )}
      </div>

      {/* Quick Actions */}
      <div className="card" style={{ marginTop: '20px' }}>
        <h2>Quick Actions</h2>
        <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap', marginTop: '20px' }}>
          <button className="btn btn-primary" onClick={() => navigate('/reservations')}>
            Manage Reservations
          </button>
          <button className="btn btn-primary" onClick={() => navigate('/rooms')}>
            View Rooms
          </button>
          <button className="btn btn-success" onClick={() => navigate('/reservations?status=confirmed')}>
            Pending Check-ins
          </button>
          <button className="btn btn-warning" onClick={() => navigate('/reservations?status=checked-in')}>
            Active Guests
          </button>
        </div>
      </div>
    </div>
  );
};

export default ReceptionistDashboard;
